DO $$ DECLARE loc_id NUMERIC (16, 0); sp_id NUMERIC (16, 0); BEGIN SELECT MAX(sp.station_id) INTO sp_id FROM pods.coordinate c JOIN pods.station_point sp ON sp.location_id = c.location_id WHERE ROUND(c.X_COORD,6)*1000000000+ROUND(c.Y_COORD,6)=ROUND({X},6)*1000000000+ROUND({Y},6) AND sp.route_id != 0;
IF sp_id IS NULL THEN INSERT INTO PODS.LOCATION(SRV_DISTRICT_GCL) VALUES ({LPU_ID}) RETURNING LOCATION_ID INTO loc_id;
INSERT INTO PODS.COORDINATE (COORDINATE_SOURCE_ID, TYPE_CL, LOCATION_ID, X_COORD, Y_COORD, Z_COORD, HORIZ_INDICATOR_LF, VERT_INDICATOR_LF, CURRENT_INDICATOR_LF, EFFECTIVE_FROM_DATE, PODS_USER, CREATE_DATE) VALUES (0, 'USR', loc_id, {X}, {Y},{Z}, 'Y', 'N', 'Y', current_timestamp, '{PODS_USER}',current_timestamp);
