                    Y_COORD = {Y}
                    WHERE LOCATION_ID IN (SELECT sp.LOCATION_ID
                    FROM pods.event_range e
                    JOIN pods.station_point sp ON sp.station_id = e.station_id_begin
                    WHERE EVENT_ID = {ID});

                    UPDATE PODS.LOCATION
                    SET SRV_DISTRICT_GCL = {LPU_ID}
                    WHERE LOCATION_ID IN (SELECT sp.LOCATION_ID
                    FROM pods.event_range e
                    JOIN pods.station_point sp ON sp.station_id = e.station_id_begin
                    WHERE e.event_id = {ID});
                    END $$;

