                    WHERE ROUND(c.X_COORD,6)*1000000000+ROUND(c.Y_COORD,6)=ROUND({X},6)*1000000000+ROUND({Y},6)
                    AND sp.route_id != 0;

                    IF sp_id IS NULL THEN
                    INSERT INTO PODS.LOCATION(SRV_DISTRICT_GCL)
                    VALUES ({LPU_ID})
                    RETURNING LOCATION_ID INTO loc_id;

                    INSERT INTO PODS.COORDINATE  (COORDINATE_SOURCE_ID, TYPE_CL,
                    LOCATION_ID, X_COORD, Y_COORD, Z_COORD, HORIZ_INDICATOR_LF,
                    VERT_INDICATOR_LF, CURRENT_INDICATOR_LF, EFFECTIVE_FROM_DATE, PODS_USER, CREATE_DATE)
                    VALUES (0, 'USR', loc_id, {X}, {Y}, {Z}, 'Y', 'N', 'Y', current_timestamp, '{PODS_USER}',current_timestamp);

                    INSERT INTO PODS.STATION_POINT (TYPE_CL, LOCATION_ID, LINE_ID,
                    SERIES, STATION, SERIES_ID, ROUTE_ID, MEASURE, PODS_USER, CREATE_DATE)
                    VALUES ('ST_POINT_TYPE_02', loc_id, 0, 0, 0, 0, 0, 0, '{PODS_USER}',current_timestamp)
                    RETURNING STATION_ID INTO sp_id;
                    END IF;

		    IF {PIPE_SEGMENT_LENGTH}>{PIPE_SEGMENT_LENGTH_LIMIT} 
	  	    THEN
	   		RAISE EXCEPTION 'При создании объекта слоя "Секции труб" превышена максимально допустимая длина объекта({PIPE_SEGMENT_LENGTH_LIMIT} метров).' USING HINT = 'Создайте несколько меньших по длине объектов';
	  	    END if;

	  	    UPDATE PODS.EVENT_RANGE
        	    SET LENGTH = {PIPE_SEGMENT_LENGTH}
       		    WHERE EVENT_ID = {ID};

                    IF {SEQ}=0 THEN
                    UPDATE PODS.EVENT_RANGE
                    SET STATION_ID_BEGIN = sp_id, STATION_ID_END = sp_id
                    WHERE EVENT_ID = {ID};
                    ELSE
                    UPDATE PODS.EVENT_RANGE
                    SET STATION_ID_END = sp_id
                    WHERE EVENT_ID = {ID};
                    END IF;
                    END IF;
                    END $$;

                    DO $$
                    DECLARE
                    loc_id   NUMERIC (16, 0);
                    sp_id    NUMERIC (16, 0);
                    BEGIN
                    IF {SEQ}=0 OR {BACK_SEQ}=0 THEN
                    SELECT MAX(sp.station_id) INTO sp_id
                    FROM pods.coordinate  c
                    JOIN pods.station_point sp ON sp.location_id = c.location_id
                    WHERE ROUND(c.X_COORD,6)*1000000000+ROUND(c.Y_COORD,6)=ROUND({X},6)*1000000000+ROUND({Y},6)
                    AND sp.route_id != 0;

                    IF sp_id IS NULL THEN
                    INSERT INTO PODS.LOCATION(SRV_DISTRICT_GCL)
                    VALUES ({LPU_ID})
                    RETURNING LOCATION_ID INTO loc_id;

                    INSERT INTO PODS.COORDINATE  (COORDINATE_SOURCE_ID, TYPE_CL,
                    LOCATION_ID, X_COORD, Y_COORD, Z_COORD, HORIZ_INDICATOR_LF,
                    VERT_INDICATOR_LF, CURRENT_INDICATOR_LF, EFFECTIVE_FROM_DATE, PODS_USER, CREATE_DATE)
                    VALUES (0, 'USR', loc_id, {X}, {Y}, {Z}, 'Y', 'N', 'Y', current_timestamp, '{PODS_USER}',current_timestamp);

                    INSERT INTO PODS.STATION_POINT (TYPE_CL, LOCATION_ID, LINE_ID,
                    SERIES, STATION, SERIES_ID, ROUTE_ID, MEASURE, PODS_USER, CREATE_DATE)
                    VALUES ('ST_POINT_TYPE_02', loc_id, 0, 0, 0, 0, 0, 0, '{PODS_USER}',current_timestamp)
                    RETURNING STATION_ID INTO sp_id;
                    END IF;

 		    IF {PIPE_SEGMENT_LENGTH}>{PIPE_SEGMENT_LENGTH_LIMIT} 
	  	    THEN
	   		RAISE EXCEPTION 'При редактировании объекта слоя "Секции труб" превышена максимально допустимая длина объекта({PIPE_SEGMENT_LENGTH_LIMIT} метров).' USING HINT = 'Создайте несколько меньших по длине объектов';
	  	    END if;

	  	    UPDATE PODS.EVENT_RANGE
        	    SET LENGTH = {PIPE_SEGMENT_LENGTH}
       		    WHERE EVENT_ID = {ID};

                    IF {SEQ}=0 THEN
                    UPDATE PODS.EVENT_RANGE
                    SET STATION_ID_BEGIN = sp_id, STATION_ID_END = sp_id
                    WHERE EVENT_ID = {ID};
                    ELSE
                    UPDATE PODS.EVENT_RANGE
                    SET STATION_ID_END = sp_id
                    WHERE EVENT_ID = {ID};
                    END IF;
                    END IF;
                    END $$;

DO $$ DECLARE loc_id NUMERIC (16, 0); sp_id NUMERIC (16, 0); BEGIN SELECT MAX(sp.station_id) INTO sp_id FROM pods.coordinate c JOIN pods.station_point sp ON sp.location_id = c.location_id WHERE ROUND(c.X_COORD,6)*1000000000+ROUND(c.Y_COORD,6)=ROUND({X},6)*1000000000+ROUND({Y},6) AND sp.route_id != 0;
IF sp_id IS NULL THEN INSERT INTO PODS.LOCATION(SRV_DISTRICT_GCL) VALUES ({LPU_ID}) RETURNING LOCATION_ID INTO loc_id;
INSERT INTO PODS.COORDINATE (COORDINATE_SOURCE_ID, TYPE_CL, LOCATION_ID, X_COORD, Y_COORD, Z_COORD, HORIZ_INDICATOR_LF, VERT_INDICATOR_LF, CURRENT_INDICATOR_LF, EFFECTIVE_FROM_DATE, PODS_USER, CREATE_DATE) VALUES (0, 'USR', loc_id, {X}, {Y},{Z}, 'Y', 'N', 'Y', current_timestamp, '{PODS_USER}',current_timestamp);
INSERT INTO PODS.STATION_POINT (TYPE_CL, LOCATION_ID, LINE_ID, SERIES, STATION, SERIES_ID, ROUTE_ID, MEASURE, PODS_USER, CREATE_DATE) VALUES ('ST_POINT_TYPE_02', loc_id, 0, 0, 0, 0, 0, 0, '{PODS_USER}',current_timestamp) RETURNING STATION_ID INTO sp_id; END IF;
UPDATE PODS.EVENT_RANGE SET STATION_ID_BEGIN = sp_id, STATION_ID_END = sp_id WHERE EVENT_ID = {ID}; END $$;

