geoCommand.object:
					DO $$
					DECLARE
					shapeId integer;
					BEGIN
					INSERT INTO PODS.SHAPE(SHAPE_GUID) VALUES(NULL)
					RETURNING SHAPE_ID INTO shapeId;

					UPDATE PODS.GEOGRAPHIC_ENTITY SET SHAPE_ID = shapeId WHERE ENTITY_ID = {ID};
					END $$;

geoCommand.part:
DO $$
DECLARE
	shapeId integer;
BEGIN
	SELECT MAX(SHAPE_ID) INTO shapeId FROM PODS.SHAPE;
	INSERT INTO PODS.PART(SHAPE_ID, HOLE_FL) VALUES(shapeId, 'N');
END $$;

geoCommand.coord:
					DO $$
					DECLARE
					LOC_ID numeric(16,0);
					partId integer;
					BEGIN
					SELECT MAX(PART_ID) INTO partId FROM PODS.PART;
					INSERT INTO PODS.LOCATION(SRV_DISTRICT_GCL)
					VALUES ({LPU_ID})
					RETURNING LOCATION_ID INTO LOC_ID;

					INSERT INTO PODS.VERTEX (PART_ID, LOCATION_ID, SEQUENCE_NO, MEASURE)
					VALUES(partId, LOC_ID, {SEQ}, {MEASURE});

					INSERT INTO PODS.COORDINATE  (COORDINATE_SOURCE_ID, TYPE_CL,
					LOCATION_ID, X_COORD, Y_COORD, Z_COORD, HORIZ_INDICATOR_LF,
					VERT_INDICATOR_LF, CURRENT_INDICATOR_LF, EFFECTIVE_FROM_DATE, PODS_USER, CREATE_DATE)
					VALUES (0, 'USR', LOC_ID, {X}, {Y}, {Z}, 'Y', 'N', 'Y', current_timestamp, '{PODS_USER}',current_timestamp);
					END $$;

geoCommand.object:
					DO $$
					DECLARE
					shapeId integer;
					BEGIN
					SELECT MAX(shape_id) INTO shapeId
					FROM pods.geographic_entity WHERE entity_id = {ID};

					UPDATE pods.coordinate
					SET CURRENT_INDICATOR_LF = 'N'
					WHERE location_id IN (SELECT v.location_id
					FROM pods.vertex v
					JOIN pods.part p ON p.part_id = v.part_id
					WHERE p.shape_id = shapeId);

					DELETE FROM pods.vertex WHERE part_id IN (SELECT p.part_id
					FROM pods.part p
					WHERE p.shape_id = shapeId);

					DELETE FROM pods.part WHERE shape_id = shapeId;
					END $$;

