                    FROM pods.offline_event WHERE offline_id = {ID};

                    UPDATE pods.offline_event SET shape_id = NULL WHERE offline_id = {ID};

                    UPDATE pods.coordinate
                    SET CURRENT_INDICATOR_LF = 'N'
