                    FROM pods.part p
                    WHERE p.shape_id = var_shape_id);

                    DELETE FROM pods.part WHERE shape_id = var_shape_id;

                    DELETE FROM pods.shape WHERE shape_id = var_shape_id;
                    END $$;

