DO $$ DECLARE returnId NUMERIC default -50; returnInspectionId NUMERIC; comp_id varchar(10) default 'UNKNOWN'; v_routeID NUMERIC default null; comp_cnt NUMERIC; BEGIN
if (select count(*) from pods.ili_tool_vendor_cl where description like '{COMPANY}')>0 then select code into comp_id from pods.ili_tool_vendor_cl where description like '{COMPANY}'; end if;
WITH routes AS (SELECT r.route_id, l.description descr, MIN(s.station_begin) km_start, MAX(s.station_end) km_end FROM pods.route r JOIN pods.series s ON s.route_id = r.route_id JOIN pods.line l ON r.line_id = l.line_id WHERE r.current_indicator_lf like 'Y' and r.type_cl IN ('ROUTE_TYPE_01','ROUTE_TYPE_04','ROUTE_TYPE_10','ROUTE_TYPE_11','ROUTE_TYPE_12') GROUP BY r.route_id, l.description), inspections AS (SELECT '{PIPE}' descr ,{KM_START} km_start ,{KM_END} km_end) SELECT MIN(route_id) route_id into v_routeID FROM routes r JOIN inspections i ON REPLACE(REPLACE(REPLACE(REPLACE (REPLACE (REPLACE (REPLACE (LOWER (LTRIM (RTRIM (i.descr::text))) ,'iv','4'), 'iii','3'),'ii','2'),'i','1'),'-',''),'-',''),' ','') = REPLACE(REPLACE(REPLACE(REPLACE (REPLACE (REPLACE (REPLACE (LOWER (LTRIM (RTRIM (r.descr))) ,'iv','4'), 'iii','3'),'ii','2'),'i','1'),'-',''),'-',''),' ','')
AND i.km_start::numeric BETWEEN r.km_start AND r.km_end AND i.km_end::numeric BETWEEN r.km_start AND r.km_end;
INSERT INTO PODS.ILI_INSPECTION(BEGIN_DATE, END_DATE, START_ODOMETER, END_ODOMETER, TOOL_TYPE_CL, MODEL, SENSOR_GCL, SENSOR_SPACING_MIN, SENSOR_SPACING_MAX, MIN_TEMP, MAX_TEMP, AVG_TEMP, MIN_VELOCITY, MAX_VELOCITY, AVG_VELOCITY, SAMPLING_FREQUENCY, RESOLUTION, RATED_MAX_VELOCITY, RATED_MAX_WT, CLUSTER_RULE_CL, SOURCE_GCL, COMMENTS, TOOL_VENDOR_CL) VALUES (TO_DATE(substring('{DATE}',1,10),'DD.MM.YYYY'), TO_DATE(substring('{DATE}',1,10),'DD.MM.YYYY'), null, null, 'UNKNOWN', '{MODEL}', 'UNKNOWN', null, null, null, null, null, null, null, null, null, null, null, null, 'UNKNOWN', 'UNKNOWN', 'INFOTEH', comp_id) RETURNING ili_inspection_id INTO returnInspectionId;
IF v_routeID IS NOT NULL THEN INSERT INTO pods.event_range(FEATURE_ID,STATION_ID_BEGIN,STATION_ID_END,PODS_USER,CREATE_DATE,EFFECTIVE_FROM_DATE,CURRENT_INDICATOR_LF,POSITIONING_TYPE_CL) VALUES ('ILI_INSP_RNG', (SELECT MIN(station_id) FROM pods.station_point sp WHERE sp.route_id = v_routeID AND sp.measure = (SELECT MIN(spp.measure) FROM pods.station_point spp WHERE spp.route_id = v_routeID )), (SELECT MAX(station_id) FROM pods.station_point sp WHERE sp.route_id = v_routeID AND sp.measure = (SELECT MAX(spp.measure) FROM pods.station_point spp WHERE spp.route_id = v_routeID )), '{config.main.PODS_USER}', current_timestamp, current_timestamp, 'Y', 'UNKNOWN') RETURNING EVENT_ID INTO returnId; END IF;
INSERT INTO PODS.ILI_INSPECTION_RANGE(EVENT_ID,sequence,ascending,ILI_INSPECTION_ID,DESCRIPTION,COMMENTS,WELD_INCREMENT,SOURCE_GCL,START_ODOMETER, END_ODOMETER, LAUNCHER_EVENT_ID, RECEIVER_EVENT_ID, WELD_START_NUMBER) VALUES(returnId,1,1,returnInspectionId,substring('{PIPE}',1,50),'{KM_START}-{KM_END}',null,'UNKNOWN',null, null, (SELECT MIN(lr.event_id) FROM pods.launcher_receiver lr JOIN pods.event_range e ON e.event_id = lr.event_id JOIN pods.station_point sp ON sp.station_id = e.station_id_begin WHERE sp.route_id = v_routeID AND lr.type_cl = 'LAUNCH_R_TYPE_06'), (SELECT MAX(lr.event_id) from pods.launcher_receiver lr JOIN pods.event_range e ON e.event_id = lr.event_id JOIN pods.station_point sp ON sp.station_id = e.station_id_begin WHERE sp.route_id = v_routeID AND lr.type_cl = 'LAUNCH_R_TYPE_07'), '{call_complex_method.FIRST_WELD_NUMBER}'); END$$;

