DO $$
	DECLARE
     v_station_begin NUMERIC;
     v_station_end   NUMERIC;
     v_r_event_id    NUMERIC;
     v_r_route_id    NUMERIC;
     v_r_series_id   NUMERIC;
     v_retcode       Integer DEFAULT 1;
			Begin
if {v_route_id} is not null then
	SELECT coalesce(MIN(sp1.station_id),0)
              ,coalesce(MAX(sp2.station_id),0)
          INTO v_station_begin, v_station_end
          FROM pods.route r
               JOIN pods.station_point sp1
                 ON sp1.route_id = r.route_id
               JOIN pods.station_point sp2
                 ON sp2.route_id = r.route_id
         WHERE r.route_id = {v_route_id}
           AND NOT EXISTS (SELECT 1
                             FROM pods.station_point sp
                            WHERE sp.route_id = {v_route_id}
                              AND sp.measure < sp1.measure)
           AND NOT EXISTS (SELECT 1
                             FROM pods.station_point sp
                            WHERE sp.route_id = {v_route_id}
                              AND sp.measure > sp2.measure)
         GROUP BY r.route_id;

       INSERT INTO pods.event_range (feature_id,station_id_begin,station_id_end,pods_user
                                    ,create_date,effective_from_date,current_indicator_lf)
              VALUES ('ILI_INSP_RNG', v_station_begin, v_station_end, 'ADMIN', current_timestamp, current_timestamp, 'Y')
              RETURNING event_id into v_r_event_id;

       UPDATE pods.ili_inspection_range
          SET event_id = v_r_event_id
       WHERE ili_inspection_id = {v_inspection_id};
end if;
			END $$;

DO $$
DECLARE v_station_begin NUMERIC; v_station_end NUMERIC; v_r_event_id NUMERIC; v_r_route_id NUMERIC; v_r_series_id NUMERIC; v_retcode NUMERIC DEFAULT 1; v_ili_inspection_range_id NUMERIC; v_launcher_event_id NUMERIC DEFAULT NULL; v_receiver_event_id NUMERIC DEFAULT NULL; Begin
UPDATE pods.ili_data dst SET anomaly_extension_cl = coalesce((SELECT a.code FROM pods.anomaly_extension_cl a JOIN pods.ili_anomaly_type_cl b ON a.code = b.index_description WHERE b.code = dst.anomaly_type_cl),'ANOMALY_EXT_004') WHERE dst.ili_inspection_id = {db_command_err.CMD.ILI_INSPECTION_ID}; /*перенесено сюда из старого инсертаиз БД Инфотеха*/
UPDATE pods.ili_data dst set anomaly_type_cl = (SELECT coalesce(MIN (CODE),0) FROM pods.ili_anomaly_type_cl t WHERE UPPER (t.description || ',' || t.anomaly_references) LIKE '%'||REGEXP_REPLACE (UPPER (TRIM (dst.feature_description)),'\(.*\)|[*.?]|\s+','%')|| '%') WHERE dst.EVENT_ID IS NULL AND dst.ili_inspection_id = {db_command_err.CMD.ILI_INSPECTION_ID};
/*Далее то, что и раньше было в скрипте интеграции в постобработке*/
update pods.ili_data d set us_weld_distance=d.absolute_odometer-d.us_weld_odometer ,ds_weld_distance=d.ds_weld_odometer - d.absolute_odometer where EXISTS (select 1 from pods.ili_data ddd JOIN pods.ili_data ww ON ww.ili_inspection_id = ddd.ili_inspection_id AND ddd.absolute_odometer>=ww.absolute_odometer AND ww.absolute_odometer>=ddd.absolute_odometer-100 WHERE ww.ili_inspection_id = {db_command_err.CMD.ILI_INSPECTION_ID} AND ww.anomaly_type_cl = 5001 AND ddd.ili_data_id = d.ili_data_id);
/* PIPE_LENGTH!!!!!!!*/
INSERT INTO pods.ili_pipe_length(ili_inspection_id, weld_number, sequence_number, start_odometer ,end_odometer, measured_wall_thickness, nominal_wall_thickness ,source_gcl,description) (SELECT ili_inspection_id ,weld_number ,ROW_NUMBER () OVER (PARTITION BY ili_inspection_id ORDER BY absolute_odometer) sequence_number ,absolute_odometer start_odometer ,LEAD (absolute_odometer) OVER (PARTITION BY ili_inspection_id ORDER BY absolute_odometer) end_odometer ,measured_wall_thickness ,nominal_wall_thickness ,source_gcl ,comments FROM pods.ili_data d WHERE anomaly_type_cl = '5001' AND ili_inspection_id = {db_command_err.CMD.ILI_INSPECTION_ID});
insert INTO pods.comments (table_id,comment_by,id,comments,source_gcl,current_indicator_lf) SELECT DISTINCT 'PIPE_SYSTEM_CL' table_id,'IS_DIRTY' comment_by,psb.pipe_system_id id,'1' , 'INFOTEH' source_gcl,'Y' current_indicator_lf FROM pods.ili_inspection_range ir JOIN pods.event_range er ON ir.event_id = er.event_id JOIN pods.station_point sp ON sp.station_id = er.station_id_begin JOIN pods.series s ON s.series = sp.series JOIN pods.pipe_system_boundary psb ON psb.line_id = s.line_id AND psb.km_end >=s.station_begin AND psb.km_begin <=s.station_end WHERE ili_inspection_id = {db_command_err.CMD.ILI_INSPECTION_ID};
/*Установка камер приема-запуска:*/ SELECT MIN(ili_inspection_range_id) INTO v_ili_inspection_range_id FROM pods.ili_inspection_range WHERE ili_inspection_id = {db_command_err.CMD.ILI_INSPECTION_ID} AND event_id != -50;
SELECT MIN(LR.EVENT_ID) INTO v_launcher_event_id FROM PODS.ILI_INSPECTION_RANGE IR JOIN PODS.EVENT_RANGE ER ON IR.EVENT_ID = ER.EVENT_ID JOIN PODS.STATION_POINT SPB ON ER.STATION_ID_BEGIN = SPB.STATION_ID JOIN PODS.STATION_POINT SPL ON SPB.ROUTE_ID = SPL.ROUTE_ID JOIN PODS.EVENT_RANGE ERL ON ERL.STATION_ID_BEGIN = SPL.STATION_ID JOIN PODS.LAUNCHER_RECEIVER LR ON ERL.EVENT_ID = LR.EVENT_ID WHERE ERL.FEATURE_ID = 'LAUNC_REC' AND LR.TYPE_CL = 'LAUNCH_R_TYPE_06' /* Запуск */ AND SPB.STATION = SPL.STATION AND IR.ILI_INSPECTION_RANGE_ID = v_ili_inspection_range_id;
SELECT MIN(LR.EVENT_ID) INTO v_receiver_event_id FROM PODS.ILI_INSPECTION_RANGE IR JOIN PODS.EVENT_RANGE ER ON IR.EVENT_ID = ER.EVENT_ID JOIN PODS.STATION_POINT SPB ON ER.STATION_ID_BEGIN = SPB.STATION_ID JOIN PODS.STATION_POINT SPL ON SPB.ROUTE_ID = SPL.ROUTE_ID JOIN PODS.EVENT_RANGE ERL ON ERL.STATION_ID_BEGIN = SPL.STATION_ID JOIN PODS.LAUNCHER_RECEIVER LR ON ERL.EVENT_ID = LR.EVENT_ID WHERE ERL.FEATURE_ID = 'LAUNC_REC' AND LR.TYPE_CL = 'LAUNCH_R_TYPE_07' /* Прием */ AND SPB.STATION = SPL.STATION AND IR.ILI_INSPECTION_RANGE_ID = v_ili_inspection_range_id;
UPDATE PODS.ILI_INSPECTION_RANGE SET LAUNCHER_EVENT_ID = v_launcher_event_id ,RECEIVER_EVENT_ID = v_receiver_event_id WHERE ILI_INSPECTION_RANGE_ID = v_ili_inspection_range_id;
END $$;

do $$
DECLARE
  id integer;
  loc_id integer;
  sp_id integer;
  c_id integer;
  min_measure integer;
  min_station_id integer;
  min_line_id integer;
  min_series integer;
  min_route_id integer;
  min_series_id integer;
  min_description  integer;
  max_measure integer;
  max_station_id  integer;
  max_description  integer;
  cur_measure integer;
  cur_route_id integer;
  cur_line_id integer;
  cur_series integer;
  cur_series_id integer;
  prev_x_coord integer;
  prev_y_coord integer;
  prev_z_coord integer;
  prev_srv_district_gcl integer;
  prev_depth_of_cover integer;
  prev_measure integer;
  prev_station_id integer;
  prev_station integer;
  next_x_coord integer;
  next_y_coord integer;
  next_z_coord integer;
  next_srv_district_gcl integer;
  next_depth_of_cover integer;
  next_measure integer;
  next_station_id integer;
  next_station integer;
  cur_x_coord integer;
  cur_y_coord integer;
  cur_station integer;
  cur_z_coord integer;
  cur_depth_of_cover integer;
  max_cnt integer;
  min_cnt integer;
  srv_id integer;
  i record;
  r record;
  BEGIN

/* привязка замеров при условии наличия в них координат - не сделано, координаты необходимо вставлять в какую-либо колонку через копипасту и оттуда разбирать на вставку сюда
  for i in (select null DEPTH,!!!rc.dd_grad SRV_DISTRICT_GCL,c."COORD_B" coord_b, c."COORD_L" coord_l,этих данных в типовой таблице pi_cis_reading нет!!!rc.event_id
  from pods.pi_cis_reading rc where c."PI_CP_EVENT_ID"=192977679)
    loop

  SELECT MAX(c.coordinate_id),MAX(c.location_id)
  INTO c_id,loc_id
  FROM pods.coordinate c
  WHERE abs(c.X_COORD-i.coord_b)<0.000001 and abs(c.Y_COORD-i.coord_l)<0.000001;

  IF c_id IS NULL THEN
   INSERT INTO pods.location(DEPTH_OF_COVER, SRV_DISTRICT_GCL)
   VALUES (null, i.SRV_DISTRICT_GCL::numeric)
   RETURNING LOCATION_ID INTO loc_id;

   INSERT INTO pods.coordinate(COORDINATE_SOURCE_ID, LOCATION_ID, X_COORD, Y_COORD, Z_COORD,
   TYPE_CL, HORIZ_INDICATOR_LF, VERT_INDICATOR_LF, CURRENT_INDICATOR_LF,
   EFFECTIVE_FROM_DATE, ACCURACY_CL, VALIDITY_TOLERANCE, CREATE_DATE, PODS_USER)
   VALUES (9, loc_id, i.coord_b, i.coord_l, null, 'ASG', 'Y', 'Y', 'N', current_timestamp, 'MEDIUM', 0, current_timestamp, 'ADMIN')
   RETURNING COORDINATE_ID INTO c_id;
  END IF;

  INSERT INTO pods.station_point(LINE_ID,ROUTE_ID,SERIES_ID,SERIES,LOCATION_ID,STATION,MEASURE,TYPE_CL)
  VALUES(0,0,0,0,loc_id,-1,0,'ST_POINT_TYPE_04')
  RETURNING STATION_ID INTO sp_id;

            update pods.event_range set STATION_ID_BEGIN=sp_id,STATION_ID_END=sp_id where event_id=i.event_id;

  END loop;
*/
/*Здесь выполняется привязка для замеров, у которых не было координат. Обязательное условие - должны быть как минимум пары замеров с координатами для замеров бе зкоординат расположенных между ними
 *  ВАЖНО!! необходимо через азадчу привязки трассовых обхектов привязать замеры с координатами к трубопроводу перед этим запуском!!!*/

  for i in (select rr.description,rr.pi_cp_event_id,e.station_id_begin from pods.pi_cis_reading rr,pods.event_Range e where rr.pi_cp_event_id=192977679
  and e.event_id=rr.event_id and e.station_id_begin=0
  order by rr.description::numeric)
  loop
            BEGIN
             select ss.measure,ss.station_id,ss.line_id,ss.series,ss.route_id,ss.series_id,rr.description*1000
             into min_measure,min_station_id,min_line_id,min_series,min_route_id,min_series_id,min_description from pods.station_point ss,pods.event_Range ee,pods.pi_cis_reading rr where
             ss.route_id=(select max(s.route_id) from pods.station_point s,pods.event_Range e,pods.pi_cis_reading r where r.pi_cp_event_id=i.pi_cp_event_id and to_number(r.description*1000)<to_number(i.description)
             and e.event_id=r.event_id and s.station_id=e.station_id_begin and e.station_id_begin>0)
             and ss.measure=(select max(s.measure) from pods.station_point s,pods.event_Range e,pods.pi_cis_reading r where r.pi_cp_event_id=i.pi_cp_event_id and to_number(r.description*1000)<to_number(i.description)
             and e.event_id=r.event_id and s.station_id=e.station_id_begin and e.station_id_begin>0)
             and ss.station_id=ee.station_id_begin and ee.event_id=rr.event_id and ss.route_id>0;

             select ss.measure,ss.station_id,rr.description*1000
             into max_measure,max_station_id,max_description from pods.station_point ss,pods.event_Range ee,pods.pi_cis_reading rr where
             ss.route_id=(select min(s.route_id) from pods.station_point s,pods.event_Range e,pods.pi_cis_reading r where r.pi_cp_event_id=i.pi_cp_event_id and to_number(r.description*1000)>to_number(i.description)
             and e.event_id=r.event_id and s.station_id=e.station_id_begin and e.station_id_begin>0 and s.measure>0)
             and ss.measure=(select min(s.measure) from pods.station_point s,pods.event_Range e,pods.pi_cis_reading r where r.pi_cp_event_id=i.pi_cp_event_id and to_number(r.description*1000)>to_number(i.description)
             and e.event_id=r.event_id and s.station_id=e.station_id_begin and e.station_id_begin>0 and s.measure>0)
             and ss.station_id=ee.station_id_begin and ee.event_id=rr.event_id  and ss.route_id>0;


             cur_series:=min_series;

             cur_measure:=round(min_measure+(i.description-min_description)*(max_measure-min_measure)/(to_number(max_description)-to_number(min_description)),2);
             cur_route_id:=min_route_id;
             cur_line_id:=min_line_id;

             cur_series_id:=min_series_id;

               select c.x_coord,c.y_coord,c.z_coord,l.srv_district_gcl,l.depth_of_cover,ss.measure,ss.station_id,ss.station
               into prev_x_coord,prev_y_coord,prev_z_coord,prev_srv_district_gcl,prev_depth_of_cover,prev_measure,prev_station_id,prev_station from pods.station_point ss,pods.coordinate c,pods.location l
                where ss.route_id=cur_route_id
               and ss.measure=(select max(s.measure) from pods.station_point s where s.measure<=cur_measure and s.route_id=ss.route_id)
               and ss.location_id=c.location_id limit 1;


             select c.x_coord,c.y_coord,c.z_coord,l.srv_district_gcl,l.depth_of_cover,ss.measure,ss.station_id,ss.station
             into next_x_coord,next_y_coord,next_z_coord,next_srv_district_gcl,next_depth_of_cover,next_measure,next_station_id,next_station from pods.station_point ss,pods.coordinate c,pods.location l
              where
             ss.route_id=cur_route_id
             and ss.measure=(select min(s.measure) from pods.station_point s where s.measure>cur_measure  and s.route_id=ss.route_id and s.measure>0)
             and ss.location_id=c.location_id  limit 1;


             cur_x_coord:=round(prev_x_coord+(next_x_coord-prev_x_coord)*(cur_measure-prev_measure)/(next_measure-prev_measure),7);
             cur_y_coord:=round(prev_y_coord+(next_y_coord-prev_y_coord)*(cur_measure-prev_measure)/(next_measure-prev_measure),7);
             cur_station:=round(prev_station+(next_station-prev_station)*(cur_measure-prev_measure)/(next_measure-prev_measure),2);
             cur_z_coord:=round(prev_z_coord+(next_z_coord-prev_z_coord)*(cur_measure-prev_measure)/(next_measure-prev_measure),2);
             cur_depth_of_cover:=round(prev_depth_of_cover+(next_depth_of_cover-prev_depth_of_cover)*(cur_measure-prev_measure)/(next_measure-prev_measure),2);

              INSERT INTO pods.location(DEPTH_OF_COVER, SRV_DISTRICT_GCL)
               VALUES (cur_depth_of_cover, prev_srv_district_gcl)
               RETURNING LOCATION_ID INTO loc_id;

               INSERT INTO pods.coordinate(COORDINATE_SOURCE_ID, LOCATION_ID, X_COORD, Y_COORD, Z_COORD,
               TYPE_CL, HORIZ_INDICATOR_LF, VERT_INDICATOR_LF, CURRENT_INDICATOR_LF,
               EFFECTIVE_FROM_DATE, ACCURACY_CL, VALIDITY_TOLERANCE, CREATE_DATE, PODS_USER)
               VALUES (9, loc_id, cur_x_coord, cur_y_coord, cur_z_coord, 'ASG', 'Y', 'Y', 'Y', current_timestamp, 'MEDIUM', 0, current_timestamp, 'ADMIN')
               RETURNING COORDINATE_ID INTO c_id;

               INSERT INTO pods.station_point(LINE_ID,ROUTE_ID,SERIES_ID,SERIES,LOCATION_ID,STATION,MEASURE,TYPE_CL)
               VALUES(cur_line_id,cur_route_id,cur_series_id,cur_series,loc_id,cur_station,cur_measure,'ST_POINT_TYPE_04')
                RETURNING STATION_ID INTO sp_id;

            update pods.event_range set STATION_ID_BEGIN=sp_id,STATION_ID_END=sp_id where event_id=i.event_id;

    EXCEPTION WHEN OTHERS
    THEN RAISE EXCEPTION ' произошла ошибка: % код %',SQLERRM, SQLSTATE ;
       END;
   END loop;
 END $$;

DO $$

DECLARE
            i record;
			ir record;
            c_max_delta CONSTANT NUMERIC DEFAULT 0.35;
            v_start_id   NUMERIC;
            v_end_id     NUMERIC;
            v_start_x    NUMERIC;
            v_start_y    NUMERIC;
            v_end_x      NUMERIC;
            v_end_y      NUMERIC;
            v_delta      NUMERIC;
            v_station_id NUMERIC;

            BEGIN

			if {ROUTE_ID} is null then 
			 for ir IN
			  (select r.route_id from pods.route r,pods.series s  where s.route_id=r.route_id and r.line_id={LINE_ID} and r.type_cl in ('ROUTE_TYPE_04','ROUTE_TYPE_10','ROUTE_TYPE_11','ROUTE_TYPE_12')
				and ((s.station_begin>={STATION_BEGIN} and  {STATION_END}<=s.station_end ) or (s.station_begin<={STATION_BEGIN} and {STATION_END}<=s.station_end ) or (s.station_begin>={STATION_BEGIN} and {STATION_END}>=s.station_end )))
			  loop
			   FOR i IN
                (select ABS(coalesce(comments::numeric,0)) km,r.event_id from pods.pi_cp_reading r where r.pi_cp_event_id = {REPORT_ID})
                 loop
                  select spp.station_id,ABS(spp.station - i.km)
                   into v_station_id,v_delta  from pods.station_point spp
                  where spp.route_id=ir.route_id and ABS(spp.station - i.km)  = (select min(ABS(sp.station - i.km)) from pods.station_point sp where sp.route_id=ir.route_id);

                 if v_station_id is not null and v_delta<c_max_delta  then
                  update pods.event_Range set station_id_begin=v_station_id,station_id_end=v_station_id where event_id=i.event_id;
                end if;
               END loop;
			 end loop;
			else 
			  UPDATE PODS.EVENT_RANGE E
				SET STATION_ID_BEGIN =
				(SELECT STATION_ID
					FROM PODS.STATION_POINT S
					WHERE     S.ROUTE_ID = {ROUTE_ID}
						AND S.MEASURE = (SELECT MIN (MEASURE)
                                      FROM PODS.STATION_POINT SP
                                     WHERE SP.ROUTE_ID = {ROUTE_ID})
                   limit 1),
				STATION_ID_END =
				(SELECT STATION_ID
					FROM PODS.STATION_POINT S
					WHERE     S.ROUTE_ID = {ROUTE_ID}
                   AND S.MEASURE = (SELECT MAX (MEASURE)
                                      FROM PODS.STATION_POINT SP
                                     WHERE SP.ROUTE_ID = {ROUTE_ID})
                   limit 1)
					WHERE E.EVENT_ID ={REPORT_ID};

				FOR i IN
				(select ABS(coalesce(substring(comments, 1, strpos(comments, ';')-1)::numeric,0)) km,r.event_id from
				pods.pi_cp_reading r where r.pi_cp_event_id = {REPORT_ID})
				loop
					select spp.station_id,ABS(spp.station - i.km)
					into v_station_id,v_delta  from pods.station_point spp
					where spp.route_id={ROUTE_ID} and ABS(spp.station - i.km)  =
                        (select min(ABS(sp.station - i.km)) from pods.station_point sp where sp.route_id={ROUTE_ID});

					if v_station_id is not null and v_delta<c_max_delta  then
						update pods.event_Range set station_id_begin=v_station_id,station_id_end=v_station_id where event_id=i.event_id;
					end if;
				END loop;
			end if;
    EXCEPTION WHEN OTHERS
    THEN RAISE EXCEPTION ' произошла ошибка: % код %',SQLERRM, SQLSTATE ;

    END $$;

	DO $$


	DECLARE
	r record;
	c_radius CONSTANT NUMERIC DEFAULT 0.25;
	v_ref_test_lead_event_id NUMERIC;
	v_station_id NUMERIC;
	v_distance NUMERIC;
	v_measure NUMERIC;
	v_sequence NUMERIC;

	v_first_station_id NUMERIC;
	v_last_station_id NUMERIC;

	c_test_lead CURSOR (cp_km NUMERIC,cp_line_id NUMERIC, cp_radius NUMERIC, cp_report_id NUMERIC )
	for SELECT er.event_id ref_test_lead_event_id,sp.station_id,abs( sp.station- cp_km) distance,sp.measure,r.sequence
	FROM pods.event_range er
	JOIN pods.station_point sp
	ON er.station_id_begin = sp.station_id
	JOIN pods.series s
	ON sp.series_id = s.series_id 
	JOIN pods.route r
	ON s.route_id = r.route_id 
	WHERE er.feature_id = 'TEST_LEAD'
	AND r.line_id = cp_line_id
	AND sp.station BETWEEN cp_km - cp_radius AND cp_km + cp_radius
	ORDER BY abs(sp.station -cp_km ),sp.measure;

	c_valve CURSOR (cp_km NUMERIC,cp_line_id NUMERIC, cp_radius NUMERIC, cp_report_id NUMERIC )
	for SELECT sp.station_id,abs( sp.station- cp_km) distance,sp.measure,r.sequence
	FROM pods.event_range er
	JOIN pods.station_point sp
	ON er.station_id_begin = sp.station_id
	JOIN pods.series s
	ON sp.series_id = s.series_id 
	JOIN pods.route r
	ON s.route_id = r.route_id 
	WHERE er.feature_id = 'VALVE'
	AND r.line_id = cp_line_id
	AND sp.station BETWEEN cp_km - cp_radius AND cp_km + cp_radius
	ORDER BY abs(sp .station -cp_km ),sp.measure;

	c_other_sp CURSOR (cp_km NUMERIC,cp_line_id NUMERIC, cp_radius NUMERIC, cp_report_id NUMERIC )
	for SELECT sp.station_id,abs( sp.station- cp_km) distance,sp.measure,r.sequence
	FROM pods.station_point sp
	JOIN pods.series s
	ON sp.series_id = s.series_id 
	JOIN pods.route r
	ON s.route_id = r.route_id 
	WHERE r.line_id = cp_line_id
	AND sp.station BETWEEN cp_km - cp_radius AND cp_km + cp_radius
	ORDER BY abs(sp .station -cp_km ),sp.measure;



	BEGIN

	create or replace function pg_temp.is_downstream_measures (p_station_id NUMERIC, p_report_id NUMERIC) RETURNS BOOLEAN as '
	DECLARE
	v_measures_downstream INTEGER; 
	v_result BOOLEAN DEFAULT FALSE;
	BEGIN

	WITH af 
	AS (SELECT rr.sequence * 1e8 + sp.measure artifical_measure,station_id
	FROM pods.pi_cp_Reading crc
	JOIN pods.event_range erc
	ON crc.event_id = erc.event_id 
	JOIN pods.station_point sp
	ON erc.station_id_Begin = sp.station_id
	JOIN pods.series s
	ON s.series_id = sp.series_id
	JOIN pods.route rr
	ON s.route_id = rr.route_id 
	WHERE crc.pi_cp_event_id = p_report_id)
	SELECT count(*)
	INTO v_measures_downstream
	FROM af 
	WHERE af.artifical_measure >= (SELECT af1.artifical_measure
	FROM af af1
	WHERE af1.station_id = p_station_id); 

	IF v_measures_downstream >0
	THEN
	v_result := TRUE;
	END IF;

	RETURN v_result; 

	END ' language plpgsql;

	IF {db_scalar_reader.LINE_ID} = -1
	THEN
	/* Не привязано. Выходим*/ 
	RETURN;
	END IF;

	/* По-новому рисую изученость.Полностью line*/
	select station_id INTO v_first_station_id from (
	SELECT sp.station_id, ROW_NUMBER() OVER( ORDER BY rr.sequence,sp.measure ) rn
	FROM pods.line l
	JOIN pods.route rr
	ON l.line_id = rr.line_id
	AND l.type_cl = CASE rr.type_cl
	WHEN 'ROUTE_TYPE_10' THEN 'LINE_TYPE_01'
	WHEN 'ROUTE_TYPE_11' THEN 'LINE_TYPE_02'
	WHEN 'ROUTE_TYPE_12' THEN 'LINE_TYPE_03'
	WHEN 'ROUTE_TYPE_04' THEN 'LINE_TYPE_04'
	END
	JOIN pods.series s
	ON rr.route_id = s.route_id 
	JOIN pods.station_point sp
	ON sp.series_id = s.series_id 
	WHERE l.line_id = {db_scalar_reader.LINE_ID} 
	AND sp.type_Cl in ('ST_POINT_TYPE_01','ST_POINT_TYPE_02','ST_POINT_TYPE_03')) tr where rn=1;

	select station_id into v_last_station_id from (
	SELECT sp.station_id, ROW_NUMBER() OVER( ORDER BY rr.sequence*100000+sp.measure desc) rn
	FROM pods.line l
	JOIN pods.route rr
	ON l.line_id = rr.line_id
	AND l.type_cl = CASE rr.type_cl
	WHEN 'ROUTE_TYPE_10' THEN 'LINE_TYPE_01'
	WHEN 'ROUTE_TYPE_11' THEN 'LINE_TYPE_02'
	WHEN 'ROUTE_TYPE_12' THEN 'LINE_TYPE_03'
	WHEN 'ROUTE_TYPE_04' THEN 'LINE_TYPE_04'
	END
	JOIN pods.series s
	ON rr.route_id = s.route_id 
	JOIN pods.station_point sp
	ON sp.series_id = s.series_id 
	WHERE l.line_id = {db_scalar_reader.LINE_ID}  
	AND sp.type_Cl in ('ST_POINT_TYPE_01','ST_POINT_TYPE_02','ST_POINT_TYPE_03')) tr where rn=1;



	FOR r IN SELECT event_id ,substr(comments ,1,strpos( comments, ';')-1 ) km
	,comments
	,trim(substr(comments ,strpos( comments, ';')+1)) control_type
	FROM PODS .PI_CP_READING
	WHERE pi_cp_event_id = {P_REPORT_ID}
	ORDER BY (substr(comments ,1,strpos( comments, ';')-1 ))::numeric
	LOOP
	BEGIN
	v_ref_test_lead_event_id := NULL;
	v_station_id := NULL;
	IF r.control_type IN ('КИП с ЭНЭС' ,'КИП со СМЭС' ,'КИП' ,'КИП, БСЗ' ,'КИП,БСЗ(труба+патрон)' ,'КИП с ЭНЕС (ВЭИ)'
	,'КИП - ГАЗ' ,'КИП с БПИ' ,'КИП с ЭНЕС,БДРМ' ,'КИК' ,'КИП - обсадная колонна' ,'КИП-АЗ'
	,'КУ(крановый узел)' ,'КП (ковер)' ,'КУЖ83' ,'КДП , БСЗ' ,'БДР-М1' ,'БДРМ 25-2-21-У1' ,'БДР-М'
	,'КПТМ' ,'КИП со СМЭС, БДРМ' ,'КДП' ,'Вход ГРС' ,'Нестандартный')
	THEN 
	OPEN c_test_lead (r .km ,{db_scalar_reader.LINE_ID} ,c_radius,{P_REPORT_ID} );
	FETCH c_test_lead INTO v_ref_test_lead_event_id,v_station_id, v_distance,v_measure,v_sequence;

	LOOP
	IF is_downstream_measures (v_station_id, {P_REPORT_ID})
	THEN 
	/* Уже на этой точке или далее висит измерение
	беру следующую */ 
	FETCH c_test_lead INTO v_ref_test_lead_event_id,v_station_id, v_distance,v_measure,v_sequence;
	/* Есть риск, что точек больше нет, нужно сбрасывать в NULL и выходить */
	IF c_test_lead%NOTFOUND
	THEN
	v_ref_test_lead_event_id := NULL;
	v_station_id := NULL;
	EXIT;
	END IF;
	ELSE
	EXIT;
	END IF; 
	END LOOP;

	CLOSE c_test_lead ;

	IF v_ref_test_lead_event_id IS NOT NULL
	THEN 
	UPDATE pods.pi_cp_reading
	SET ref_test_lead_event_id = v_ref_test_lead_event_id
	WHERE event_id = r .event_id;
	END IF; 

	ELSIF r.control_type = 'КУ(крановый узел)'
	THEN 
	OPEN c_valve (r .km ,{db_scalar_reader.LINE_ID} ,c_radius,{P_REPORT_ID} );
	FETCH c_valve INTO v_station_id, v_distance,v_measure,v_sequence;

	LOOP
	IF is_downstream_measures (v_station_id, {P_REPORT_ID})
	THEN 
	/* Уже на этой точке или далее висит измерение
	беру следующую */ 
	FETCH c_valve INTO v_station_id, v_distance,v_measure,v_sequence; 
	/* Есть риск, что точек больше нет, нужно сбрасывать в NULL и выходить */
	IF c_valve%NOTFOUND
	THEN
	v_station_id := NULL;
	EXIT;
	END IF;
	ELSE
	RAISE INFO 'v_station_id: %',v_station_id ; 
        EXIT;
	END IF; 
	END LOOP; 

	CLOSE c_valve ;
	END IF; 

	IF v_station_id IS NULL
	THEN
	/* Не нашли ни КИП, ни крана */
	OPEN c_other_sp (r .km ,{db_scalar_reader.LINE_ID} ,c_radius,{P_REPORT_ID} );
	FETCH c_other_sp INTO v_station_id, v_distance,v_measure,v_sequence;

	LOOP

	IF is_downstream_measures (v_station_id, {P_REPORT_ID})
	THEN 
	/* Уже на этой точке или далее висит измерение
	беру следующую */ 
	FETCH c_other_sp INTO v_station_id, v_distance,v_measure,v_sequence;
	/* Есть риск, что точек больше нет, нужно сбрасывать в NULL и выходить */
	IF c_other_sp%NOTFOUND
	THEN
	v_station_id := NULL;
	EXIT;
	END IF;
	ELSE
	RAISE INFO 'v_station_id: %',v_station_id ; 
	EXIT;
	END IF; 
	END LOOP; 

	CLOSE c_other_sp ;

	END IF;

	IF v_station_id IS NOT NULL
	THEN
	/* Прицепляю измерение на точки найденного объекта, КИП или КРАН */
	UPDATE pods.event_range
	SET station_id_begin = v_station_id
	,station_id_end = v_station_id
	,effective_from_date = current_timestamp
	,pods_user = USER
	WHERE event_id = r .event_id ;
		RAISE INFO 'UPDATE pods.event_range' ; 
	END IF; 

	EXCEPTION
	WHEN OTHERS THEN
	RAISE NOTICE 'При обработке измерения event_id=:%  произошла ошибка: %',r.event_id||''||SQLERRM, SQLSTATE;
	END;
	END LOOP;

	END $$;

			DO $$
			DECLARE
 			km NUMERIC(10,2);
 			lc NUMERIC(10,2);
 			BEGIN
 			DELETE FROM WEB50.PS_INDEX_OBJ WHERE KEY_VALUES = '{src_id}';

 			SELECT sp1.measure+{fract}*(sp2.measure-sp1.measure),sp1.station+{fract}*(sp2.station-sp1.station) INTO lc,km
 			FROM pods.station_point sp1, pods.station_point sp2
 			WHERE sp1.station_id = {s_id} AND sp2.station_id = {e_id};

 			INSERT INTO WEB50.PS_INDEX_OBJ(TEMPLATE_ID, PIPE_SYSTEM_ID, STATE_ID, KEY_FIELDS, KEY_VALUES, KM_START, KM_END, LINE_COORD_START, LINE_COORD_END, DISTANCE, GID, CUSER_ID) 
 			VALUES(1, {id}, 0, 'EVENT_ID', '{src_id}', km, km, lc, lc, {dist}, NULL, 0);
 			END $$;

			DO $$

			DECLARE
 			sp_id NUMERIC(16,0);
 			loc_id NUMERIC(16,0);
 			c_id NUMERIC(16,0);
 			e_id NUMERIC(16,0);
 			BEGIN
 			IF {NEAREST_DIST}<{config.main.COORD_TOLERANCE} 
			THEN
 			   c_id := {COORDINATE_ID};
 			   loc_id := {LOCATION_ID};
			   sp_id := {STATION_ID};
 			ELSE
    			     INSERT INTO pods.location(DEPTH_OF_COVER, SRV_DISTRICT_GCL) 
 			     VALUES ({DEPTH}, {SRV_DISTRICT_GCL}) 
 			     RETURNING LOCATION_ID INTO loc_id;

   			     INSERT INTO pods.coordinate(COORDINATE_SOURCE_ID, LOCATION_ID, X_COORD, Y_COORD, Z_COORD, 
 			     TYPE_CL, HORIZ_INDICATOR_LF, VERT_INDICATOR_LF, CURRENT_INDICATOR_LF, 
 			     EFFECTIVE_FROM_DATE, ACCURACY_CL, VALIDITY_TOLERANCE, CREATE_DATE, PODS_USER) 
 			     VALUES (9, loc_id, {X}, {Y}, {Z}, 'ASG', 'Y', 'Y', 'Y', current_timestamp, 'MEDIUM', {ACCURACY}, current_timestamp, '{config.main.PODS_USER}')
 			     RETURNING COORDINATE_ID INTO c_id;
			END IF;

 			IF sp_id is null  THEN
 			  INSERT INTO pods.station_point(LINE_ID,ROUTE_ID,SERIES_ID,SERIES,LOCATION_ID,STATION,MEASURE,TYPE_CL) 
 			  VALUES({LINE_ID},{ROUTE_ID},{SERIES_ID},{SERIES},loc_id,{STATION},{MEASURE},'ST_POINT_TYPE_04') 
 			  RETURNING STATION_ID INTO sp_id;
                        end if;

 			INSERT INTO pods.event_range(FEATURE_ID,STATION_ID_BEGIN,STATION_ID_END,PODS_USER,CREATE_DATE,VALIDITY_TOLERANCE,EFFECTIVE_FROM_DATE,CURRENT_INDICATOR_LF,POSITIONING_TYPE_CL) 
 			VALUES ('ILI_DATA', sp_id, sp_id, '{config.main.PODS_USER}', current_timestamp, {ACCURACY}, current_timestamp, 'Y', 'UNKNOWN') 
 			RETURNING EVENT_ID INTO e_id;

 			UPDATE pods.ili_data 
 			SET coordinate_id = c_id, event_id = e_id, CALIBRATED_MEASURE = {MEASURE}
 			WHERE ili_data_id = {ILI_DATA_ID};
 			END $$;

			DO $$
			DECLARE
 			km NUMERIC(10,2);
 			lc NUMERIC(10,2);
 			BEGIN
 			DELETE FROM WEB50.PS_INDEX_OBJ WHERE KEY_VALUES = '{src_id}';

 			SELECT sp1.measure+{fract}*(sp2.measure-sp1.measure),sp1.station+{fract}*(sp2.station-sp1.station)
 			INTO lc,km
 			FROM pods.station_point sp1, pods.station_point sp2
 			WHERE sp1.station_id = {s_id} AND sp2.station_id = {e_id};

 			INSERT INTO WEB50.PS_INDEX_OBJ(TEMPLATE_ID, PIPE_SYSTEM_ID, STATE_ID, KEY_FIELDS, KEY_VALUES, KM_START, KM_END, LINE_COORD_START, LINE_COORD_END, DISTANCE, GID, CUSER_ID) 
 			VALUES(1, {id}, 0, 'ILI_DATA_ID', '{src_id}', km, km, lc, lc, {dist}, NULL, 0);
 			END $$;

UPDATE pods.ili_pipe_length 
SET EVENT_ID = {EVENT_ID}, START_COORDINATE_ID = {COORDINATE_ID}, END_COORDINATE_ID = {NEXT_COORDINATE_ID}
WHERE ILI_PIPE_LENGTH_ID = {ILI_PIPE_LENGTH_ID}

DO $$ BEGIN
INSERT INTO pods.event_range(ORIGINAL_EVENT_ID,FEATURE_ID,STATION_ID_BEGIN,STATION_ID_END,LENGTH,PODS_USER,CREATE_DATE,VALIDITY_TOLERANCE,EFFECTIVE_FROM_DATE,CURRENT_INDICATOR_LF,POSITIONING_TYPE_CL) SELECT e.EVENT_ID,'PIPE_LENGTH', e.STATION_ID_BEGIN,e.STATION_ID_END,e.LENGTH,'{config.main.PODS_USER}',current_timestamp,e.VALIDITY_TOLERANCE,current_timestamp,e.CURRENT_INDICATOR_LF,e.POSITIONING_TYPE_CL FROM pods.event_range e JOIN pods.ili_pipe_length pl on pl.event_id = e.event_id WHERE pl.ili_inspection_id = {P_REPORT_ID};
UPDATE pods.ili_pipe_length pl SET ref_event_id = (SELECT MIN (re.event_id) FROM pods.event_range re WHERE re.original_event_id = pl.event_id), comments = 'EQ' WHERE pl.ili_inspection_id = {P_REPORT_ID};
UPDATE pods.event_range SET original_event_id = null WHERE event_id IN (SELECT pl.ref_event_id FROM pods.ili_pipe_length pl WHERE pl.ili_inspection_id = {P_REPORT_ID});
INSERT INTO pods.pipe_length(EVENT_ID, LENGTH_IDENTIFIER, DESCRIPTION, SOURCE_GCL, END_TREATMENT_CL, COMMENTS) SELECT pl.ref_event_id, pl.weld_number, pl.description, pl.source_gcl, 'UNKNOWN','ВТД:'||t.description||'('||i.begin_date||')' FROM pods.ili_pipe_length pl JOIN pods.ili_inspection i ON i.ili_inspection_id = pl.ili_inspection_id JOIN pods.ili_tool_vendor_cl t ON t.code = i.tool_vendor_cl WHERE i.ili_inspection_id = {P_REPORT_ID} AND pl.event_id IS NOT NULL;
END $$;

UPDATE PODS.STATION_POINT
SET STATION = {STATION}
WHERE STATION_ID = {STATION_ID}

UPDATE PODS.STATION_POINT
SET STATION = {STATION}
WHERE STATION_ID = {STATION_ID}

UPDATE PODS.STATION_POINT
SET MEASURE = {MEASURE}
WHERE STATION_ID = {STATION_ID}

UPDATE PODS.STATION_POINT
SET MEASURE = {MEASURE}
WHERE STATION_ID = {STATION_ID}

			DO $$
			DECLARE
 			v_dummy VARCHAR(512); 
 			v_event_id NUMERIC;
 			v_calc_id NUMERIC;
 			BEGIN
 			IF {CALC_ID} IS NOT NULL
 			THEN 
 			/* Если calc_id уже заполнен, значит мы просто сохраняем результаты расчета, без создания новых объектов*/ 
 			UPDATE pods.sto_ehz_calc 
 			SET DANGER_DEG = '{DANGER_DEG}' 
 			,DANGER_DEG_SEGMENT_TYPE = '{DANGER_DEG_SEGMENT_TYPE}'
 			,DEFEAT_ZONE = {DEFEAT_ZONE}
 			,INTEGR_DEG_RATE = {INTEGR_DEG_RATE}
 			,UNEVENNESS_COEF = {UNEVENNESS_COEF}
 			,SEGMENT_INTENSIVE_DEG_RATE = {SEGMENT_INTENSIVE_DEG_RATE}
 			,SEGMENT_INTENSIVITY = '{SEGMENT_INTENSIVITY}'
 			,SEGMENT_CATEGORY = '{SEGMENT_CATEGORY}'
 			,CORR_AVER_DEPTH = {CORR_AVER_DEPTH}
 			,CORR_AVER_SPEED = {CORR_AVER_SPEED}
 			,CORR_CRITICAL_DEPTH = {CORR_CRITICAL_DEPTH}
 			,CORR_TIME_TO_CRITICAL_DEPTH = {CORR_TIME_TO_CRITICAL_DEPTH}
 			,PRIORITY_RATE = {PRIORITY_RATE}
 			,IS_DEFEAT_DANGER = {IS_DEFEAT_DANGER}
 			,RING_BREAK_POINT = {RING_BREAK_POINT}
 			,TIME_TO_DANGER = {TIME_TO_DANGER}
 			,DEPTH_TO_DANGER = {DEPTH_TO_DANGER}
 			,LENGTH_TO_DANGER = {LENGTH_TO_DANGER}
 			,WIDTH_TO_DANGER = {WIDTH_TO_DANGER}
 			,SQUARE_TO_DANGER = {SQUARE_TO_DANGER}
 			,BEGIN_ODOMETER = {BEGIN_ODOMETER}
 			,END_ODOMETER = {END_ODOMETER}
 			WHERE calc_id = {CALC_ID};
 			ELSE 
 			/* Добавляю запись в расчетную таблицу */ 
 			INSERT INTO pods.sto_ehz_calc
 			(EVENT_ID,TYPE_CL,ILI_INSPECTION_ID,BEGIN_EVENT_ID,END_EVENT_ID,DANGER_DEG,DANGER_DEG_SEGMENT_TYPE,DEFEAT_ZONE,INTEGR_DEG_RATE,UNEVENNESS_COEF 
 			,SEGMENT_INTENSIVE_DEG_RATE,SEGMENT_INTENSIVITY,SEGMENT_CATEGORY,CORR_AVER_DEPTH,CORR_AVER_SPEED,CORR_CRITICAL_DEPTH,CORR_TIME_TO_CRITICAL_DEPTH 
 			,PRIORITY_RATE,IS_DEFEAT_DANGER,RING_BREAK_POINT,TIME_TO_DANGER,DEPTH_TO_DANGER,LENGTH_TO_DANGER,WIDTH_TO_DANGER,SQUARE_TO_DANGER,BEGIN_ODOMETER,END_ODOMETER)
 			VALUES 
 			({EVENT_ID},'{TYPE_CL}',{ILI_INSPECTION_ID},{BEGIN_EVENT_ID},{END_EVENT_ID},'{DANGER_DEG}','{DANGER_DEG_SEGMENT_TYPE}',{DEFEAT_ZONE},{INTEGR_DEG_RATE},{UNEVENNESS_COEF}
 			,{SEGMENT_INTENSIVE_DEG_RATE},'{SEGMENT_INTENSIVITY}','{SEGMENT_CATEGORY}',{CORR_AVER_DEPTH},{CORR_AVER_SPEED},{CORR_CRITICAL_DEPTH},{CORR_TIME_TO_CRITICAL_DEPTH}
 			,{PRIORITY_RATE},{IS_DEFEAT_DANGER},{RING_BREAK_POINT},{TIME_TO_DANGER},{DEPTH_TO_DANGER},{LENGTH_TO_DANGER},{WIDTH_TO_DANGER},{SQUARE_TO_DANGER},{BEGIN_ODOMETER},{END_ODOMETER})
 			RETURNING CALC_ID INTO v_calc_id;

 			END IF;
 			END $$;

DO $$ DECLARE BEGIN IF '{CALC_CONSTRUCTION_INTERVALS}'='true' THEN UPDATE pods.event_range er SET current_indicator_lf = 'N' WHERE feature_id = 'INTERVAL_PER_OBJ' AND current_indicator_lf = 'Y' AND EXISTS (SELECT 1 FROM pods.sto_ehz_calc ic WHERE ic.event_id = er.event_id AND ic.ili_inspection_id = {INSPECTION_ID});
END IF;
IF '{CALC_REGULAR_INTERVALS}'='true' THEN UPDATE pods.event_range er SET current_indicator_lf = 'N' WHERE feature_id = 'INTERVAL_REGULAR' AND current_indicator_lf = 'Y' AND EXISTS (SELECT 1 FROM pods.sto_ehz_calc ic WHERE ic.event_id = er.event_id AND ic.ili_inspection_id = {INSPECTION_ID}); END IF;
IF '{CALC_LINE_EVENTS}' = 'true' THEN UPDATE pods.event_range er SET current_indicator_lf = 'N' WHERE feature_id = 'INTERVAL_LAN_REC' AND current_indicator_lf = 'Y' AND EXISTS (SELECT 1 FROM pods.sto_ehz_calc ic WHERE ic.event_id = er.event_id AND ic.ili_inspection_id = {INSPECTION_ID}); END IF;
DELETE FROM pods.sto_ehz_calc WHERE ili_inspection_id = {INSPECTION_ID};
END $$;

DO $$
DECLARE i RECORD; v_event_id NUMERIC; v_report_measure_begin NUMERIC(10,2); v_report_measure_end NUMERIC(10,2); v_begin_odometer NUMERIC(10,2); v_end_odometer NUMERIC(10,2); v_odometer1 NUMERIC(10,2); v_odometer2 NUMERIC(10,2); v_measure1 NUMERIC(10,2); v_measure2 NUMERIC(10,2); v_route_id NUMERIC;
BEGIN
SELECT s.route_id,spb.measure,spe.measure INTO v_route_id,v_report_measure_begin,v_report_measure_end FROM pods.series s JOIN pods.station_point spb ON s.series_id = spb.series_id JOIN pods.event_range er ON er.station_id_begin = spb.station_id JOIN pods.station_point spe ON er.station_id_end = spe.station_id JOIN pods.ili_inspection_range ir ON ir.event_id = er.event_id
WHERE ir.ili_inspection_id = {INSPECTION_ID};
IF v_route_id IS NULL OR v_route_id = 0 THEN RETURN; END IF;
FOR i IN (SELECT er.*,spb.measure measure_begin,spe.measure measure_end FROM pods.event_range er JOIN pods.station_point spb ON er.station_id_begin = spb.station_id JOIN pods.station_point spe ON er.station_id_end = spe.station_id
WHERE event_id in ({LINE_EVENTS_QUERY})) LOOP
/* в свете последних решений необходимо обрезать по размерам отчета */
/* BEGIN ODOMETER*/ /* Получена точка на трубе, создана или найдена, не важно, необходимо получить значение одометра в этом месте*/ BEGIN SELECT absolute_odometer,measure INTO v_odometer1,v_measure1 FROM (SELECT ROW_NUMBER() OVER (ORDER BY sp1.measure DESC,d.absolute_odometer ASC) RWN /* mssql compatibility*/ ,d.absolute_odometer ,sp1.measure FROM pods.station_point sp1 JOIN pods.station_point spb USING (series_id) JOIN pods.event_range er ON sp1.station_id = er.station_id_begin JOIN pods.ili_data d USING (event_id) WHERE spb.station_id = i.station_id_begin AND sp1.measure<=spb.measure AND er.current_indicator_lf = 'Y' AND d.ili_inspection_id = {INSPECTION_ID}) d WHERE rwn = 1; EXCEPTION WHEN NO_DATA_FOUND THEN /* не нашлось ничего вокруг, попробую экстраполировать */ v_odometer1 := -1e6; v_measure1 := -1e6; END;
BEGIN SELECT absolute_odometer,measure INTO v_odometer2,v_measure2 FROM (SELECT ROW_NUMBER() OVER (ORDER BY sp1.measure ASC,d.absolute_odometer DESC) RWN /* mssql compatibility*/ ,d.absolute_odometer ,sp1.measure FROM pods.station_point sp1 JOIN pods.station_point spb USING (series_id) JOIN pods.event_range er ON sp1.station_id = er.station_id_begin JOIN pods.ili_data d USING (event_id) WHERE spb.station_id = i.station_id_begin AND sp1.measure>=spb.measure AND er.current_indicator_lf = 'Y' AND d.ili_inspection_id = {INSPECTION_ID}) d WHERE rwn = 1; EXCEPTION WHEN NO_DATA_FOUND THEN /* не нашлось ничего вокруг, попробую экстраполировать */ v_odometer2 := 1e6; v_measure2 := 1e6; END;
IF v_measure2 != v_measure1 THEN v_begin_odometer := v_odometer1+(i.measure_begin-v_measure1)*(v_odometer2-v_odometer1)/(v_measure2-v_measure1); ELSE v_begin_odometer := v_odometer1; END IF;
/* END ODOMETER*/ /* Получена точка на трубе, создана или найдена, не важно, необходимо получить значение одометра в этом месте*/ BEGIN SELECT absolute_odometer,measure INTO v_odometer1,v_measure1 FROM (SELECT ROW_NUMBER() OVER (ORDER BY sp1.measure DESC,d.absolute_odometer ASC) RWN /* mssql compatibility*/ ,d.absolute_odometer ,sp1.measure FROM pods.station_point sp1 JOIN pods.station_point spb USING (series_id) JOIN pods.event_range er ON sp1.station_id = er.station_id_begin JOIN pods.ili_data d USING (event_id) WHERE spb.station_id = i.station_id_end AND sp1.measure<=spb.measure AND er.current_indicator_lf = 'Y' AND d.ili_inspection_id = {INSPECTION_ID}) d WHERE rwn = 1; EXCEPTION WHEN NO_DATA_FOUND THEN /* не нашлось ничего вокруг, попробую экстраполировать */ v_odometer1 := -1e6; v_measure1 := -1e6; END;
BEGIN SELECT absolute_odometer,measure INTO v_odometer2,v_measure2 FROM (SELECT ROW_NUMBER() OVER (ORDER BY sp1.measure ASC,d.absolute_odometer DESC) RWN /* mssql compatibility*/ ,d.absolute_odometer ,sp1.measure FROM pods.station_point sp1 JOIN pods.station_point spb USING (series_id) JOIN pods.event_range er ON sp1.station_id = er.station_id_begin JOIN pods.ili_data d USING (event_id) WHERE spb.station_id = i.station_id_end AND sp1.measure>=spb.measure AND er.current_indicator_lf = 'Y' AND d.ili_inspection_id = {INSPECTION_ID}) d WHERE rwn = 1; EXCEPTION WHEN NO_DATA_FOUND THEN /* не нашлось ничего вокруг, попробую экстраполировать */ v_odometer2 := 1e6; v_measure2 := 1e6; END;
IF v_measure2 != v_measure1 THEN v_end_odometer := v_odometer1+(i.measure_end-v_measure1)*(v_odometer2-v_odometer1)/(v_measure2-v_measure1); ELSE v_end_odometer := v_odometer1; END IF;
INSERT INTO pods.event_range(feature_id,station_id_begin,station_id_end,pods_user,create_date,effective_from_date,current_indicator_lf,positioning_type_cl,length) VALUES ('INTERVAL_LAN_REC',i.station_id_begin,i.station_id_end,'{config.main.PODS_USER}', current_timestamp, current_timestamp, 'Y', 'UNKNOWN',/*i.length*/ ABS(i.measure_end-i.measure_begin)) RETURNING event_id INTO v_event_id;
INSERT INTO pods.sto_ehz_calc (EVENT_ID,ILI_INSPECTION_ID,TYPE_CL,BEGIN_ODOMETER,END_ODOMETER) VALUES (v_event_id,{INSPECTION_ID},'Оценка состояния по линейному event-объекту',v_begin_odometer,v_end_odometer);
END LOOP; END $$;

DO $$
DECLARE i RECORD; v_station_id_begin NUMERIC; v_station_id_end NUMERIC; v_station_measure_begin NUMERIC(10,2); v_station_measure_end NUMERIC(10,2); v_route_id NUMERIC; v_event_like_this Integer; v_event_id NUMERIC; v_report_measure_begin NUMERIC(10,2); v_report_measure_end NUMERIC(10,2); v_begin_odometer NUMERIC(10,2); v_end_odometer NUMERIC(10,2); v_odometer1 NUMERIC(10,2); v_odometer2 NUMERIC(10,2); v_measure1 NUMERIC(10,2); v_measure2 NUMERIC(10,2);
BEGIN
SELECT s.route_id,spb.measure,spe.measure INTO v_route_id,v_report_measure_begin,v_report_measure_end FROM pods.series s JOIN pods.station_point spb ON s.series_id = spb.series_id JOIN pods.event_range er ON er.station_id_begin = spb.station_id JOIN pods.station_point spe ON er.station_id_end = spe.station_id JOIN pods.ili_inspection_range ir ON ir.event_id = er.event_id
WHERE ir.ili_inspection_id = {INSPECTION_ID};
IF v_route_id IS NULL OR v_route_id = 0 THEN RETURN; END IF;
FOR i IN (WITH my_construction_elements AS (SELECT station_id_begin FROM pods.event_range er WHERE er.event_id in ({CONSTRUCTION_ELEMENT_QUERY}) AND er.current_indicator_lf = 'Y'), structural_elements AS (SELECT sp.station_id,sp.measure FROM pods.station_point sp JOIN pods.series s ON s.series_id = sp.series_id WHERE s.route_id = v_route_id AND sp.measure BETWEEN v_report_measure_begin AND v_report_measure_end AND EXISTS (SELECT 1 FROM my_construction_elements me WHERE me.station_id_begin = sp.station_id) UNION ALL SELECT DISTINCT FIRST_VALUE(spx.station_id) OVER (PARTITION BY er.event_id ORDER BY ABS(spx.measure - (spb.measure + (spe.measure - spb.measure)/2 ))) station_id ,FIRST_VALUE(spx.measure) OVER (PARTITION BY er.event_id ORDER BY ABS(spx.measure - (spb.measure + (spe.measure - spb.measure)/2 ))) measure FROM pods.event_range er JOIN pods.station_point spb ON spb.station_id = er.station_id_begin JOIN pods.series s ON s.series_id = spb.series_id JOIN pods.station_point spe ON spe.station_id = er.station_id_end JOIN pods.station_point spX ON s.series_id = spX.series_id AND spX.measure BETWEEN spb.measure AND spe.measure WHERE er.event_id in ({CONSTRUCTION_ELEMENT_QUERY}) AND ( (spb.measure BETWEEN v_report_measure_begin AND v_report_measure_end) OR (spe.measure BETWEEN v_report_measure_begin AND v_report_measure_end)) AND er.current_indicator_lf = 'Y' AND s.route_id = v_route_id AND er.station_id_begin != er.station_id_end), limits_station AS ( SELECT MIN(measure) first_measure ,MAX(measure) last_measure ,(select station_id from pods.station_point ss where ss.route_id= v_route_id and ss.measure=(select min(measure) from pods.station_point sss where sss.route_id= v_route_id) limit 1) first_station_id ,(select station_id from pods.station_point ss where ss.route_id= v_route_id and ss.measure=(select max(measure) from pods.station_point sss where sss.route_id= v_route_id) limit 1) last_station_id FROM pods.station_point sp JOIN pods.series s ON s.series_id = sp.series_id WHERE s.route_id = v_route_id AND sp.measure BETWEEN v_report_measure_begin AND v_report_measure_end), combained_junctions AS (SELECT first_station_id station_id,first_measure measure FROM limits_station ls1 WHERE NOT EXISTS (SELECT 1 FROM structural_elements se WHERE ls1.first_measure = se.measure) UNION ALL SELECT station_id,measure FROM structural_elements UNION ALL SELECT last_station_id station_id,last_measure FROM limits_station ls2 WHERE NOT EXISTS (SELECT 1 FROM structural_elements se WHERE ls2.last_measure = se.measure)) SELECT station_id,measure FROM combained_junctions ORDER BY measure) LOOP v_station_id_end := i.station_id; v_station_measure_end := i.measure; /* Получена точка на трубе, создана или найдена, не важно, необходимо получить значение одометра в этом месте*/ BEGIN SELECT absolute_odometer,measure INTO v_odometer1,v_measure1 FROM (SELECT ROW_NUMBER() OVER (ORDER BY sp1.measure DESC,d.absolute_odometer ASC) RWN /* mssql compatibility*/ ,d.absolute_odometer ,sp1.measure FROM pods.station_point sp1 JOIN pods.station_point spb USING (series_id) JOIN pods.event_range er ON sp1.station_id = er.station_id_begin JOIN pods.ili_data d USING (event_id) WHERE spb.station_id = v_station_id_end AND sp1.measure<=spb.measure AND er.current_indicator_lf = 'Y' AND d.ili_inspection_id = {INSPECTION_ID}) d WHERE rwn = 1; EXCEPTION WHEN NO_DATA_FOUND THEN /* не нашлось ничего вокруг, попробую экстраполировать */ v_odometer1 := -1e6; v_measure1 := -1e6; END;
BEGIN SELECT absolute_odometer,measure INTO v_odometer2,v_measure2 FROM (SELECT ROW_NUMBER() OVER (ORDER BY sp1.measure ASC,d.absolute_odometer DESC) RWN /* mssql compatibility*/ ,d.absolute_odometer ,sp1.measure FROM pods.station_point sp1 JOIN pods.station_point spb USING (series_id) JOIN pods.event_range er ON sp1.station_id = er.station_id_begin JOIN pods.ili_data d USING (event_id) WHERE spb.station_id = v_station_id_end AND sp1.measure>=spb.measure AND er.current_indicator_lf = 'Y' AND d.ili_inspection_id = {INSPECTION_ID}) d WHERE rwn = 1; EXCEPTION WHEN NO_DATA_FOUND THEN /* не нашлось ничего вокруг, попробую экстраполировать */ v_odometer2 := 1e6; v_measure2 := 1e6; END;
IF v_measure2 != v_measure1 THEN v_end_odometer := v_odometer1+(i.measure-v_measure1)*(v_odometer2-v_odometer1)/(v_measure2-v_measure1); ELSE v_end_odometer := v_odometer1; END IF;
/* Если первый проход то пропускаем итерацию цикла */ IF v_station_id_end IS NOT NULL AND v_station_id_begin IS NOT NULL THEN
SELECT count(*) INTO v_event_like_this FROM pods.event_range er WHERE feature_id = 'INTERVAL_PER_OBJ' AND current_indicator_lf = 'Y' AND station_id_begin = v_station_id_begin AND station_id_end = v_station_id_end AND EXISTS (SELECT 1 FROM pods.sto_ehz_calc ic WHERE ic.event_id = er.event_id AND ic.ili_inspection_id = {INSPECTION_ID});
IF v_event_like_this = 0 THEN
INSERT INTO pods.event_range(feature_id,station_id_begin,station_id_end,pods_user,create_date,effective_from_date,current_indicator_lf,positioning_type_cl,length) VALUES ('INTERVAL_PER_OBJ',v_station_id_begin,v_station_id_end,'{config.main.PODS_USER}', current_timestamp, current_timestamp, 'Y', 'UNKNOWN',ABS(v_station_measure_end-v_station_measure_begin)) RETURNING event_id INTO v_event_id;
INSERT INTO pods.sto_ehz_calc (EVENT_ID,ILI_INSPECTION_ID,TYPE_CL,BEGIN_ODOMETER,END_ODOMETER) VALUES (v_event_id,{INSPECTION_ID},'Оценка состояния по межконструкционным участкам',v_begin_odometer,v_end_odometer);
END IF; END IF; v_station_id_begin := v_station_id_end; v_station_measure_begin := v_station_measure_end; v_begin_odometer := v_end_odometer;
END LOOP; EXCEPTION WHEN OTHERS THEN RAISE EXCEPTION ' произошла ошибка: % код %',SQLERRM, SQLSTATE ; END $$;

DO $$
DECLARE i RECORD; v_station_id_begin NUMERIC; v_station_id_end NUMERIC; v_location_id NUMERIC; v_srv_district_gcl NUMERIC; v_depth_of_cover NUMERIC; v_X NUMERIC; v_Y NUMERIC; v_Z NUMERIC;
v_depth_of_cover1 NUMERIC; v_X1 NUMERIC; v_Y1 NUMERIC; v_Z1 NUMERIC; v_measure1 NUMERIC(10,2); v_station_id1 NUMERIC;
v_depth_of_cover2 NUMERIC; v_X2 NUMERIC; v_Y2 NUMERIC; v_Z2 NUMERIC; v_measure2 NUMERIC(10,2); v_station_id2 NUMERIC;
v_route_id NUMERIC; v_event_like_this Integer; v_event_id NUMERIC; v_report_measure_begin NUMERIC(10,2); v_report_measure_end NUMERIC(10,2); v_begin_odometer NUMERIC(10,2); v_end_odometer NUMERIC(10,2); v_odometer1 NUMERIC(10,2); v_odometer2 NUMERIC(10,2); BEGIN
SELECT s.route_id,spb.measure,spe.measure INTO v_route_id,v_report_measure_begin,v_report_measure_end FROM pods.series s JOIN pods.station_point spb ON s.series_id = spb.series_id JOIN pods.event_range er ON er.station_id_begin = spb.station_id JOIN pods.station_point spe ON er.station_id_end = spe.station_id JOIN pods.ili_inspection_range ir ON ir.event_id = er.event_id WHERE ir.ili_inspection_id = {INSPECTION_ID};
IF v_route_id IS NULL OR v_route_id = 0 THEN RETURN; END IF;
FOR i IN ( WITH row_generator AS (SELECT v_report_measure_begin +(series.a-1) * {REGULAR_INTERVALS_DISTANCE} measure FROM generate_series(1, (v_report_measure_end-v_report_measure_begin)/{REGULAR_INTERVALS_DISTANCE} + 1) as series(a)), base_sp AS (SELECT measure ,station_id ,LEAD(measure) OVER(ORDER BY measure) n_measure ,LEAD(station_id) OVER (ORDER BY measure) n_station_id ,sp.series ,sp.station ,sp.series_id ,r.route_id ,r.line_id FROM pods.station_point sp JOIN pods.series s ON s.series_id = sp.series_id JOIN pods.route r ON r.route_id = s.route_id JOIN pods.coordinate c ON c.location_id = sp.location_id WHERE r.route_id = v_route_id AND sp.type_cl IN ('ST_POINT_TYPE_01','ST_POINT_TYPE_02','ST_POINT_TYPE_03')) (SELECT sp.line_id, sp.route_id, sp.series_id, sp.series, sp.station, s.measure FROM row_generator s JOIN base_sp sp ON s.measure>=sp.measure AND s.measure<sp.n_measure UNION ALL SELECT s.line_id, s.route_id, s.series_id, MAX(sp.series) series, MAX(sp.station) station, MAX(sp.measure) measure FROM pods.series s JOIN pods.station_point sp ON sp.series_id = s.series_id JOIN PODS.coordinate c ON c.location_id = sp.location_id WHERE s.route_id = v_route_id AND sp.type_cl IN ('ST_POINT_TYPE_01','ST_POINT_TYPE_02','ST_POINT_TYPE_03') AND sp.measure BETWEEN v_report_measure_begin AND v_report_measure_end GROUP BY s.line_id, s.route_id, s.series_id, sp.series) ORDER BY MEASURE) LOOP
v_station_id_begin := v_station_id_end; v_begin_odometer := v_end_odometer;
SELECT count(*) INTO v_event_like_this FROM pods.event_range er JOIN pods.station_point spe ON er.station_id_end = spe.station_id JOIN pods.series s ON s.series_id = spe.series_id WHERE feature_id = 'INTERVAL_REGULAR' AND er.CURRENT_INDICATOR_LF = 'Y' AND spe.measure = i.measure AND spe.type_cl = 'ST_POINT_TYPE_04' AND length = {REGULAR_INTERVALS_DISTANCE} AND s.route_id = v_route_id AND EXISTS (SELECT 1 FROM pods.sto_ehz_calc ic WHERE ic.event_id = er.event_id AND ic.ili_inspection_id = {INSPECTION_ID});
IF v_event_like_this = 0 THEN
/* необходимо интерполировать значения с соседних пикетов*/
SELECT l.srv_district_gcl ,l.depth_of_cover ,c.x_coord ,c.y_coord ,c.z_coord ,sp.measure ,sp.station_id INTO v_srv_district_gcl ,v_depth_of_cover1 ,v_X1 ,v_Y1 ,v_Z1 ,v_measure1 ,v_station_id1 FROM pods.station_point sp JOIN pods.location l ON l.location_id = sp.location_id JOIN pods.coordinate c ON c.location_id = sp.location_id WHERE sp.route_id=i.route_id and sp.measure = (SELECT max(sp2.measure) FROM pods.station_point sp2 WHERE sp2.route_id=v_route_id AND sp2.measure <=i.measure) limit 1;
SELECT l.depth_of_cover ,c.x_coord ,c.y_coord ,c.z_coord ,sp.measure ,sp.station_id INTO v_depth_of_cover2 ,v_X2 ,v_Y2 ,v_Z2 ,v_measure2 ,v_station_id2 FROM pods.station_point sp JOIN pods.location l ON l.location_id = sp.location_id JOIN pods.coordinate c ON c.location_id = sp.location_id WHERE sp.route_id=i.route_id and sp.measure = (SELECT min(sp2.measure) FROM pods.station_point sp2 WHERE sp2.route_id=v_route_id AND sp2.measure >=i.measure) limit 1;
IF abs(v_measure2 - v_measure1)>{REGULAR_INTERVALS_DISTANCE}*0.1 THEN v_depth_of_cover := v_depth_of_cover1+(i.measure-v_measure1)*(v_depth_of_cover2-v_depth_of_cover1)/(v_measure2-v_measure1); v_X := v_X1+(i.measure-v_measure1)*(v_X2-v_X1)/(v_measure2-v_measure1); v_Y := v_Y1+(i.measure-v_measure1)*(v_Y2-v_Y1)/(v_measure2-v_measure1); v_Z := v_Z1+(i.measure-v_measure1)*(v_Z2-v_Z1)/(v_measure2-v_measure1); INSERT INTO pods.location (srv_district_gcl,depth_of_cover) VALUES (v_srv_district_gcl,v_depth_of_cover) RETURNING location_id INTO v_location_id;
INSERT INTO pods.station_point (line_id,route_id,series_id,series,location_id,station,measure,type_cl) VALUES (i.line_id,i.route_id,i.series_id,i.series,v_location_id,i.station,i.measure,'ST_POINT_TYPE_04') RETURNING station_id INTO v_station_id_end;
INSERT INTO pods.coordinate(coordinate_source_id, location_id, x_coord, y_coord, z_coord ,type_cl, horiz_indicator_lf, vert_indicator_lf, current_indicator_lf ,effective_from_date, accuracy_cl, create_date, pods_user) VALUES (9,v_location_id,v_X,v_Y,v_Z,'ASG','Y', 'Y', 'Y',current_timestamp, 'MEDIUM', current_timestamp, '{config.main.PODS_USER}'); ELSE v_station_id_end:=v_station_id1; END IF;
ELSE SELECT MIN(spe.station_id) INTO v_station_id_end FROM pods.event_range er JOIN pods.station_point spe ON er.station_id_end = spe.station_id JOIN pods.series s ON s.series_id = spe.series_id WHERE feature_id = 'INTERVAL_REGULAR' AND er.CURRENT_INDICATOR_LF = 'Y' AND spe.measure = i.measure AND spe.type_cl = 'ST_POINT_TYPE_04' AND length = {REGULAR_INTERVALS_DISTANCE} AND s.route_id = v_route_id AND EXISTS (SELECT 1 FROM pods.sto_ehz_calc ic WHERE ic.event_id = er.event_id AND ic.ili_inspection_id = {INSPECTION_ID});
END IF; /* Получена точка на трубе, создана или найдена, не важно, необходимо получить значение одометра в этом месте*/ BEGIN SELECT absolute_odometer,measure INTO v_odometer1,v_measure1 FROM (SELECT ROW_NUMBER() OVER (ORDER BY sp1.measure DESC,d.absolute_odometer ASC) RWN /* mssql compatibility*/ ,d.absolute_odometer ,sp1.measure FROM pods.station_point sp1 JOIN pods.station_point spb USING (series_id) JOIN pods.event_range er ON sp1.station_id = er.station_id_begin JOIN pods.ili_data d USING (event_id) WHERE spb.station_id = v_station_id_end AND sp1.measure<=spb.measure AND er.current_indicator_lf = 'Y' AND d.ili_inspection_id = {INSPECTION_ID}) d WHERE rwn = 1; EXCEPTION WHEN NO_DATA_FOUND THEN /* не нашлось ничего вокруг, попробую экстраполировать */ v_odometer1 := -1e6; v_measure1 := -1e6; END;
BEGIN SELECT absolute_odometer,measure INTO v_odometer2,v_measure2 FROM (SELECT ROW_NUMBER() OVER (ORDER BY sp1.measure ASC,d.absolute_odometer DESC) RWN /* mssql compatibility*/ ,d.absolute_odometer ,sp1.measure FROM pods.station_point sp1 JOIN pods.station_point spb USING (series_id) JOIN pods.event_range er ON sp1.station_id = er.station_id_begin JOIN pods.ili_data d USING (event_id) WHERE spb.station_id = v_station_id_end AND sp1.measure>=spb.measure AND er.current_indicator_lf = 'Y' AND d.ili_inspection_id = {INSPECTION_ID}) d WHERE rwn = 1; EXCEPTION WHEN NO_DATA_FOUND THEN /* не нашлось ничего вокруг, попробую экстраполировать */ v_odometer2 := 1e6; v_measure2 := 1e6; END;
IF v_measure2 != v_measure1 THEN v_end_odometer := v_odometer1+(i.measure-v_measure1)*(v_odometer2-v_odometer1)/(v_measure2-v_measure1); ELSE v_end_odometer := v_odometer1; END IF;
/* Если первый проход то пропускаем итерацию цикла */ IF v_station_id_begin IS NOT NULL THEN INSERT INTO pods.event_range(feature_id,station_id_begin,station_id_end,pods_user,create_date,effective_from_date,current_indicator_lf,positioning_type_cl,length) VALUES ('INTERVAL_REGULAR',v_station_id_begin,v_station_id_end,'{config.main.PODS_USER}', current_timestamp, current_timestamp, 'Y', 'UNKNOWN',{REGULAR_INTERVALS_DISTANCE}) RETURNING event_id INTO v_event_id;
INSERT INTO pods.sto_ehz_calc (EVENT_ID,ILI_INSPECTION_ID,TYPE_CL,BEGIN_ODOMETER,END_ODOMETER) VALUES (v_event_id,{INSPECTION_ID},'Оценка состояния по регулярным участкам ({REGULAR_INTERVALS_DISTANCE}m)',v_begin_odometer,v_end_odometer);
END IF;
END LOOP; EXCEPTION WHEN OTHERS THEN RAISE EXCEPTION ' произошла ошибка: % код %',SQLERRM, SQLSTATE ; END $$;

DO $$ DECLARE v_ili_cluster_id NUMERIC;
BEGIN
INSERT INTO pods.ili_cluster (effective_length,effective_width,effective_area,description,event_id,ili_inspection_id,comments) VALUES (CASE WHEN {EFFECTIVE_LENGTH} > 9999 THEN 9999 ELSE {EFFECTIVE_LENGTH} END ,{EFFECTIVE_WIDTH},{EFFECTIVE_AREA},'{CLUSTER_TYPE}',-110,{ILI_INSPECTION_ID},'calc anomaly count: '||{ANOMALY_COUNT}) RETURNING ili_cluster_id INTO v_ili_cluster_id ;
INSERT INTO pods.sto_ili_cluster_calc (ILI_INSPECTION_ID,ILI_CLUSTER_ID,CLUSTER_COMMENT) VALUES ({ILI_INSPECTION_ID},v_ili_cluster_id ,CASE '{CLUSTER_TYPE}' WHEN 'Группа поверхностных дефектов' THEN 'п. 8, СТО Газпром 2-2.2-595-2011 ' WHEN 'Ремонтное место' THEN 'п. 7.4, СТО Газпром 2-2.2-595-2011 ' END);
END $$;

			DO $$

			BEGIN
 			UPDATE pods.ili_data 
 			SET ili_cluster_id = {ILI_CLUSTER_ID}
 			WHERE ili_data_id = {ILI_DATA_ID};

 			UPDATE pods.sto_ili_data_calc 
 			SET ili_cluster_id = {ILI_CLUSTER_ID}
 			WHERE ili_data_id = {ILI_DATA_ID};
 			END $$;

DO $$
BEGIN
insert into pods.sto_ili_data_calc(ILI_INSPECTION_ID,ILI_DATA_ID) SELECT def.ILI_INSPECTION_ID,def.ILI_DATA_ID
FROM PODS.ILI_DATA def JOIN PODS.ILI_ANOMALY_TYPE_CL ia ON def.anomaly_type_cl=ia.code WHERE def.ILI_INSPECTION_ID = {INSPECTION_ID} AND ia.category_cl=1 ON CONFLICT (ILI_DATA_ID) DO UPDATE SET ILI_CLUSTER_ID = NULL
,CLUSTER_COMMENT = NULL ,R_595_2011_REPAIR_METHOD = NULL ,R_595_2011_DEPTH_ABRASION = NULL ,R_595_2011_SQUARE_ABRASION = NULL ,R_595_2011_LENGTH_PIPE_REPAIR = NULL ,R_595_2011_COMMENT = NULL ,STO_112_2007_ASSURANCE_FACTOR = NULL ,STO_112_2007_DESTRUCT_PRESSURE = NULL ,STO_112_2007_ALLOW_PRESSURE = NULL ,STO_112_2007_PERFORMANCE_EVAL = NULL ,STO_173_2007_CRITICAL_PRESSURE = NULL ,STO_173_2007_DANGER_DEGREE = NULL ,STO_173_2007_SAFE_PRESSURE = NULL ,STO_292_2007_DANGER_RANK = NULL ,STO_401_2009_FAIL_PROBABILITY = NULL;
END $$;

DO $$ DECLARE BEGIN
update pods.ili_cluster d set description = (SELECT CASE COUNT(*) WHEN 0 THEN 'Группа поверхностных дефектов без КРН' ELSE 'Группа поверхностных дефектов с КРН' END FROM pods.ili_data d,pods.ili_cluster icc WHERE d.ili_cluster_id =icc.ili_cluster_id and icc.description like 'Группа поверхностных дефектов%' AND icc.ili_inspection_Id = {INSPECTION_ID} AND d.anomaly_extension_cl = 'ANOMALY_EXT_005') where d.ili_cluster_id in (SELECT ic.ili_cluster_id FROM pods.ili_cluster ic WHERE description like 'Группа поверхностных дефектов%' AND ili_inspection_Id = {INSPECTION_ID});
update PODS.ILI_CLUSTER d set AVG_DEPTH = (SELECT AVG(dd.average_depth) avg_depth FROM pods.ili_data dd WHERE dd.ili_inspection_id = {INSPECTION_ID} AND dd.ili_cluster_id=d.ili_cluster_id GROUP BY dd.ili_cluster_id) ,avg_length = (SELECT CASE WHEN AVG(dd.length) > 9999 THEN 9999 ELSE AVG(dd.length) END FROM pods.ili_data dd WHERE dd.ili_inspection_id = {INSPECTION_ID} AND dd.ili_cluster_id=d.ili_cluster_id GROUP BY dd.ili_cluster_id) ,avg_orientation = (SELECT ROUND(AVG (dd.orientation_deg),1) avg_orientation FROM pods.ili_data dd WHERE dd.ili_inspection_id = {INSPECTION_ID} AND dd.ili_cluster_id =d.ili_cluster_id GROUP BY dd.ili_cluster_id) ,avg_width = (SELECT CASE WHEN AVG(dd.width) > 9999.99 THEN 9999.99 ELSE AVG(dd.width) END FROM pods.ili_data dd WHERE dd.ili_inspection_id = {INSPECTION_ID} AND dd.ili_cluster_id =d.ili_cluster_id GROUP BY dd.ili_cluster_id) ,anomaly_count = (SELECT COUNT(*) FROM pods.ili_data dd WHERE dd.ili_inspection_id = {INSPECTION_ID} AND dd.ili_cluster_id =d.ili_cluster_id GROUP BY dd.ili_cluster_id) where d.ili_cluster_id in (select dd.ili_cluster_id from pods.ili_data dd where dd.ili_inspection_id = {INSPECTION_ID});
update pods.event_range dd SET validity_tolerance =(select AVG(erD.validity_tolerance) validity_tolerance FROM pods.ili_data d JOIN pods.ili_cluster c ON d.ili_cluster_id = c.ili_cluster_id JOIN pods.event_range erC ON erC.event_id = c.event_id JOIN pods.event_range erD ON erD.event_id = d.event_id WHERE d.ili_inspection_id = {INSPECTION_ID} AND d.ili_cluster_id is not null and erC.event_id = dd.event_id GROUP BY erC.event_id) where exists (select 1 from pods.ili_data d JOIN pods.ili_cluster c ON d.ili_cluster_id = c.ili_cluster_id JOIN pods.event_range erC ON erC.event_id = c.event_id JOIN pods.event_range erD ON erD.event_id = d.event_id WHERE d.ili_inspection_id = {INSPECTION_ID} AND d.ili_cluster_id is not null and erC.event_id = dd.event_id);
END $$;

DO $$ BEGIN UPDATE pods.ili_data SET ili_Cluster_id = NULL WHERE ili_inspection_id = {INSPECTION_ID}; update pods.sto_ili_data_calc d SET ILI_CLUSTER_ID = NULL ,CLUSTER_COMMENT = NULL ,R_595_2011_REPAIR_METHOD = NULL ,R_595_2011_DEPTH_ABRASION = NULL ,R_595_2011_SQUARE_ABRASION = NULL ,R_595_2011_LENGTH_PIPE_REPAIR = NULL ,R_595_2011_COMMENT = NULL ,STO_112_2007_ASSURANCE_FACTOR = NULL ,STO_112_2007_DESTRUCT_PRESSURE = NULL ,STO_112_2007_ALLOW_PRESSURE = NULL ,STO_112_2007_PERFORMANCE_EVAL = NULL ,STO_173_2007_CRITICAL_PRESSURE = NULL ,STO_173_2007_DANGER_DEGREE = NULL ,STO_173_2007_SAFE_PRESSURE = NULL ,STO_292_2007_DANGER_RANK = NULL ,STO_401_2009_FAIL_PROBABILITY = NULL where ili_data_id in (SELECT def.ILI_DATA_ID FROM PODS.ILI_DATA def JOIN PODS.ILI_ANOMALY_TYPE_CL ia ON def.anomaly_type_cl=ia.code WHERE def.ILI_INSPECTION_ID = {INSPECTION_ID} AND ia.category_cl=1) ;
UPDATE pods.event_range SET station_id_Begin =0 ,station_id_end =0 ,current_indicator_lf = 'N' WHERE feature_id = 'ILI_CLUSTER' AND event_id IN (SELECT event_id FROM pods.ili_cluster WHERE ili_inspection_id = {INSPECTION_ID});
DELETE FROM pods.sto_ili_cluster_calc WHERE ili_inspection_id = {INSPECTION_ID};
DELETE FROM pods.sto_ili_cluster_calc_adv WHERE ili_inspection_id = {INSPECTION_ID};
DELETE FROM pods.ili_cluster WHERE ili_inspection_id = {INSPECTION_ID}; END $$;

DO $$
