DO $$
DECLARE BEGIN IF '{CALC_CONSTRUCTION_INTERVALS}'='true' THEN UPDATE pods.event_range er SET current_indicator_lf = 'N' WHERE feature_id = 'INTERVAL_PER_OBJ' AND current_indicator_lf = 'Y' AND EXISTS (SELECT 1 FROM pods.sto_ili_inspection_calc ic WHERE ic.event_id = er.event_id AND ic.ili_inspection_id = {INSPECTION_ID});
END IF;
IF '{CALC_REGULAR_INTERVALS}'='true' THEN UPDATE pods.event_range er SET current_indicator_lf = 'N' WHERE feature_id = 'INTERVAL_REGULAR' AND current_indicator_lf = 'Y' AND EXISTS (SELECT 1 FROM pods.sto_ili_inspection_calc ic WHERE ic.event_id = er.event_id AND ic.ili_inspection_id = {INSPECTION_ID}); END IF;
IF '{CALC_LINE_EVENTS}' = 'true' THEN UPDATE pods.event_range er SET current_indicator_lf = 'N' WHERE feature_id = 'INTERVAL_LAN_REC' AND current_indicator_lf = 'Y' AND EXISTS (SELECT 1 FROM pods.sto_ili_inspection_calc ic WHERE ic.event_id = er.event_id AND ic.ili_inspection_id = {INSPECTION_ID}); END IF;
DELETE FROM pods.sto_ili_inspection_calc WHERE ili_inspection_id = {INSPECTION_ID};
END $$;

DO $$ DECLARE i RECORD; v_event_id NUMERIC; v_report_measure_begin NUMERIC(10,2); v_report_measure_end NUMERIC(10,2); v_begin_odometer NUMERIC(10,2); v_end_odometer NUMERIC(10,2); v_odometer1 NUMERIC(10,2); v_odometer2 NUMERIC(10,2); v_measure1 NUMERIC(10,2); v_measure2 NUMERIC(10,2); v_route_id NUMERIC;
BEGIN
SELECT s.route_id,spb.measure,spe.measure INTO v_route_id,v_report_measure_begin,v_report_measure_end FROM pods.series s JOIN pods.station_point spb ON s.series_id = spb.series_id JOIN pods.event_range er ON er.station_id_begin = spb.station_id JOIN pods.station_point spe ON er.station_id_end = spe.station_id JOIN pods.ili_inspection_range ir ON ir.event_id = er.event_id WHERE ir.ili_inspection_id = {INSPECTION_ID};
IF v_route_id IS NULL OR v_route_id = 0 THEN RETURN; END IF;
FOR i IN (SELECT er.*,spb.measure measure_begin,spe.measure measure_end FROM pods.event_range er JOIN pods.station_point spb ON er.station_id_begin = spb.station_id JOIN pods.station_point spe ON er.station_id_end = spe.station_id WHERE event_id in ({LINE_EVENTS_QUERY})) LOOP
/* в свете последних решений необходимо обрезать по размерам отчета */
/* BEGIN ODOMETER*/ /* Получена точка на трубе, создана или найдена, не важно, необходимо получить значение одометра в этом месте*/ BEGIN SELECT absolute_odometer,measure INTO v_odometer1,v_measure1 FROM (SELECT ROW_NUMBER() OVER (ORDER BY sp1.measure DESC,d.absolute_odometer ASC) RWN /* mssql compatibility*/ ,d.absolute_odometer ,sp1.measure FROM pods.station_point sp1 JOIN pods.station_point spb USING (series_id) JOIN pods.event_range er ON sp1.station_id = er.station_id_begin JOIN pods.ili_data d USING (event_id) WHERE spb.station_id = i.station_id_begin AND sp1.measure<=spb.measure AND er.current_indicator_lf = 'Y' AND d.ili_inspection_id = {INSPECTION_ID}) d WHERE rwn = 1; EXCEPTION WHEN NO_DATA_FOUND THEN /* не нашлось ничего вокруг, попробую экстраполировать */ v_odometer1 := -1e6; v_measure1 := -1e6; END;
BEGIN SELECT absolute_odometer,measure INTO v_odometer2,v_measure2 FROM (SELECT ROW_NUMBER() OVER (ORDER BY sp1.measure ASC,d.absolute_odometer DESC) RWN /* mssql compatibility*/ ,d.absolute_odometer ,sp1.measure FROM pods.station_point sp1 JOIN pods.station_point spb USING (series_id) JOIN pods.event_range er ON sp1.station_id = er.station_id_begin JOIN pods.ili_data d USING (event_id) WHERE spb.station_id = i.station_id_begin AND sp1.measure>=spb.measure AND er.current_indicator_lf = 'Y' AND d.ili_inspection_id = {INSPECTION_ID}) d WHERE rwn = 1; EXCEPTION WHEN NO_DATA_FOUND THEN /* не нашлось ничего вокруг, попробую экстраполировать */ v_odometer2 := 1e6; v_measure2 := 1e6; END;
IF v_measure2 != v_measure1 THEN v_begin_odometer := v_odometer1+(i.measure_begin-v_measure1)*(v_odometer2-v_odometer1)/(v_measure2-v_measure1); ELSE v_begin_odometer := v_odometer1; END IF;
/* END ODOMETER*/ /* Получена точка на трубе, создана или найдена, не важно, необходимо получить значение одометра в этом месте*/ BEGIN SELECT absolute_odometer,measure INTO v_odometer1,v_measure1 FROM (SELECT ROW_NUMBER() OVER (ORDER BY sp1.measure DESC,d.absolute_odometer ASC) RWN /* mssql compatibility*/ ,d.absolute_odometer ,sp1.measure FROM pods.station_point sp1 JOIN pods.station_point spb USING (series_id) JOIN pods.event_range er ON sp1.station_id = er.station_id_begin JOIN pods.ili_data d USING (event_id) WHERE spb.station_id = i.station_id_end AND sp1.measure<=spb.measure AND er.current_indicator_lf = 'Y' AND d.ili_inspection_id = {INSPECTION_ID}) d WHERE rwn = 1; EXCEPTION WHEN NO_DATA_FOUND THEN /* не нашлось ничего вокруг, попробую экстраполировать */ v_odometer1 := -1e6; v_measure1 := -1e6; END;
BEGIN SELECT absolute_odometer,measure INTO v_odometer2,v_measure2 FROM (SELECT ROW_NUMBER() OVER (ORDER BY sp1.measure ASC,d.absolute_odometer DESC) RWN /* mssql compatibility*/ ,d.absolute_odometer ,sp1.measure FROM pods.station_point sp1 JOIN pods.station_point spb USING (series_id) JOIN pods.event_range er ON sp1.station_id = er.station_id_begin JOIN pods.ili_data d USING (event_id) WHERE spb.station_id = i.station_id_end AND sp1.measure>=spb.measure AND er.current_indicator_lf = 'Y' AND d.ili_inspection_id = {INSPECTION_ID}) d WHERE rwn = 1; EXCEPTION WHEN NO_DATA_FOUND THEN /* не нашлось ничего вокруг, попробую экстраполировать */ v_odometer2 := 1e6; v_measure2 := 1e6; END;
IF v_measure2 != v_measure1 THEN v_end_odometer := v_odometer1+(i.measure_end-v_measure1)*(v_odometer2-v_odometer1)/(v_measure2-v_measure1); ELSE v_end_odometer := v_odometer1; END IF;
INSERT INTO pods.event_range(feature_id,station_id_begin,station_id_end,pods_user,create_date,effective_from_date,current_indicator_lf,positioning_type_cl,length) VALUES ('INTERVAL_LAN_REC',i.station_id_begin,i.station_id_end,'{config.main.PODS_USER}', current_timestamp, current_timestamp, 'Y', 'UNKNOWN',/*i.length*/ ABS(i.measure_end-i.measure_begin)) RETURNING event_id INTO v_event_id;
INSERT INTO pods.sto_ili_inspection_calc (EVENT_ID,ILI_INSPECTION_ID,TYPE_CL,BEGIN_ODOMETER,END_ODOMETER) VALUES (v_event_id,{INSPECTION_ID},'Оценка состояния по линейному event-объекту',v_begin_odometer,v_end_odometer);
END LOOP; END $$;

DO $$
DECLARE i RECORD; v_station_id_begin NUMERIC; v_station_id_end NUMERIC; v_station_measure_begin NUMERIC(10,2); v_station_measure_end NUMERIC(10,2); v_route_id NUMERIC; v_event_like_this Integer; v_event_id NUMERIC; v_report_measure_begin NUMERIC(10,2); v_report_measure_end NUMERIC(10,2); v_begin_odometer NUMERIC(10,2); v_end_odometer NUMERIC(10,2); v_odometer1 NUMERIC(10,2); v_odometer2 NUMERIC(10,2); v_measure1 NUMERIC(10,2); v_measure2 NUMERIC(10,2);
BEGIN
SELECT s.route_id,spb.measure,spe.measure INTO v_route_id,v_report_measure_begin,v_report_measure_end FROM pods.series s JOIN pods.station_point spb ON s.series_id = spb.series_id JOIN pods.event_range er ON er.station_id_begin = spb.station_id JOIN pods.station_point spe ON er.station_id_end = spe.station_id JOIN pods.ili_inspection_range ir ON ir.event_id = er.event_id WHERE ir.ili_inspection_id = {INSPECTION_ID};
IF v_route_id IS NULL OR v_route_id = 0 THEN RETURN; END IF;
FOR i IN (WITH my_construction_elements AS (SELECT station_id_begin FROM pods.event_range er WHERE er.event_id in ({CONSTRUCTION_ELEMENT_QUERY}) AND er.current_indicator_lf = 'Y'), structural_elements AS (SELECT sp.station_id,sp.measure FROM pods.station_point sp JOIN pods.series s ON s.series_id = sp.series_id WHERE s.route_id = v_route_id AND sp.measure BETWEEN v_report_measure_begin AND v_report_measure_end AND EXISTS (SELECT 1 FROM my_construction_elements me WHERE me.station_id_begin = sp.station_id) UNION ALL SELECT DISTINCT FIRST_VALUE(spx.station_id) OVER (PARTITION BY er.event_id ORDER BY ABS(spx.measure - (spb.measure + (spe.measure - spb.measure)/2 ))) station_id ,FIRST_VALUE(spx.measure) OVER (PARTITION BY er.event_id ORDER BY ABS(spx.measure - (spb.measure + (spe.measure - spb.measure)/2 ))) measure FROM pods.event_range er JOIN pods.station_point spb ON spb.station_id = er.station_id_begin JOIN pods.series s ON s.series_id = spb.series_id JOIN pods.station_point spe ON spe.station_id = er.station_id_end JOIN pods.station_point spX ON s.series_id = spX.series_id AND spX.measure BETWEEN spb.measure AND spe.measure WHERE er.event_id in ({CONSTRUCTION_ELEMENT_QUERY}) AND ( (spb.measure BETWEEN v_report_measure_begin AND v_report_measure_end) OR (spe.measure BETWEEN v_report_measure_begin AND v_report_measure_end)) AND er.current_indicator_lf = 'Y' AND s.route_id = v_route_id AND er.station_id_begin != er.station_id_end), limits_station AS ( SELECT MIN(measure) first_measure ,MAX(measure) last_measure ,(select station_id from pods.station_point ss where ss.route_id= v_route_id and ss.measure=(select min(measure) from pods.station_point sss where sss.route_id= v_route_id) limit 1) first_station_id ,(select station_id from pods.station_point ss where ss.route_id= v_route_id and ss.measure=(select max(measure) from pods.station_point sss where sss.route_id= v_route_id) limit 1) last_station_id FROM pods.station_point sp JOIN pods.series s ON s.series_id = sp.series_id WHERE s.route_id = v_route_id AND sp.measure BETWEEN v_report_measure_begin AND v_report_measure_end), combained_junctions AS (SELECT first_station_id station_id,first_measure measure FROM limits_station ls1 WHERE NOT EXISTS (SELECT 1 FROM structural_elements se WHERE ls1.first_measure = se.measure) UNION ALL SELECT station_id,measure FROM structural_elements UNION ALL SELECT last_station_id station_id,last_measure FROM limits_station ls2 WHERE NOT EXISTS (SELECT 1 FROM structural_elements se WHERE ls2.last_measure = se.measure)) SELECT station_id,measure FROM combained_junctions ORDER BY measure) LOOP v_station_id_end := i.station_id; v_station_measure_end := i.measure; /* Получена точка на трубе, создана или найдена, не важно, необходимо получить значение одометра в этом месте*/ BEGIN SELECT absolute_odometer,measure INTO v_odometer1,v_measure1 FROM (SELECT ROW_NUMBER() OVER (ORDER BY sp1.measure DESC,d.absolute_odometer ASC) RWN /* mssql compatibility*/ ,d.absolute_odometer ,sp1.measure FROM pods.station_point sp1 JOIN pods.station_point spb USING (series_id) JOIN pods.event_range er ON sp1.station_id = er.station_id_begin JOIN pods.ili_data d USING (event_id) WHERE spb.station_id = v_station_id_end AND sp1.measure<=spb.measure AND er.current_indicator_lf = 'Y' AND d.ili_inspection_id = {INSPECTION_ID}) d WHERE rwn = 1; EXCEPTION WHEN NO_DATA_FOUND THEN /* не нашлось ничего вокруг, попробую экстраполировать */ v_odometer1 := -1e6; v_measure1 := -1e6; END;
BEGIN SELECT absolute_odometer,measure INTO v_odometer2,v_measure2 FROM (SELECT ROW_NUMBER() OVER (ORDER BY sp1.measure ASC,d.absolute_odometer DESC) RWN /* mssql compatibility*/ ,d.absolute_odometer ,sp1.measure FROM pods.station_point sp1 JOIN pods.station_point spb USING (series_id) JOIN pods.event_range er ON sp1.station_id = er.station_id_begin JOIN pods.ili_data d USING (event_id) WHERE spb.station_id = v_station_id_end AND sp1.measure>=spb.measure AND er.current_indicator_lf = 'Y' AND d.ili_inspection_id = {INSPECTION_ID}) d WHERE rwn = 1; EXCEPTION WHEN NO_DATA_FOUND THEN /* не нашлось ничего вокруг, попробую экстраполировать */ v_odometer2 := 1e6; v_measure2 := 1e6; END;
IF v_measure2 != v_measure1 THEN v_end_odometer := v_odometer1+(i.measure-v_measure1)*(v_odometer2-v_odometer1)/(v_measure2-v_measure1); ELSE v_end_odometer := v_odometer1; END IF;
/* Если первый проход то пропускаем итерацию цикла */ IF v_station_id_end IS NOT NULL AND v_station_id_begin IS NOT NULL THEN
SELECT count(*) INTO v_event_like_this FROM pods.event_range er WHERE feature_id = 'INTERVAL_PER_OBJ' AND current_indicator_lf = 'Y' AND station_id_begin = v_station_id_begin AND station_id_end = v_station_id_end AND EXISTS (SELECT 1 FROM pods.sto_ili_inspection_calc ic WHERE ic.event_id = er.event_id AND ic.ili_inspection_id = {INSPECTION_ID});
IF v_event_like_this = 0 THEN
INSERT INTO pods.event_range(feature_id,station_id_begin,station_id_end,pods_user,create_date,effective_from_date,current_indicator_lf,positioning_type_cl,length) VALUES ('INTERVAL_PER_OBJ',v_station_id_begin,v_station_id_end,'{config.main.PODS_USER}', current_timestamp, current_timestamp, 'Y', 'UNKNOWN',ABS(v_station_measure_end-v_station_measure_begin)) RETURNING event_id INTO v_event_id;
INSERT INTO pods.sto_ili_inspection_calc (EVENT_ID,ILI_INSPECTION_ID,TYPE_CL,BEGIN_ODOMETER,END_ODOMETER) VALUES (v_event_id,{INSPECTION_ID},'Оценка состояния по межконструкционным участкам',v_begin_odometer,v_end_odometer);
END IF; END IF; v_station_id_begin := v_station_id_end; v_station_measure_begin := v_station_measure_end; v_begin_odometer := v_end_odometer;
END LOOP; EXCEPTION WHEN OTHERS THEN RAISE EXCEPTION ' произошла ошибка: % код %',SQLERRM, SQLSTATE ; END; $$

DO $$
DECLARE i RECORD; v_station_id_begin NUMERIC; v_station_id_end NUMERIC; v_location_id NUMERIC; v_srv_district_gcl NUMERIC; v_depth_of_cover NUMERIC; v_X NUMERIC; v_Y NUMERIC; v_Z NUMERIC;
v_depth_of_cover1 NUMERIC; v_X1 NUMERIC; v_Y1 NUMERIC; v_Z1 NUMERIC; v_measure1 NUMERIC(10,2); v_station_id1 NUMERIC;
v_depth_of_cover2 NUMERIC; v_X2 NUMERIC; v_Y2 NUMERIC; v_Z2 NUMERIC; v_measure2 NUMERIC(10,2); v_station_id2 NUMERIC;
v_route_id NUMERIC; v_event_like_this Integer; v_event_id NUMERIC; v_report_measure_begin NUMERIC(10,2); v_report_measure_end NUMERIC(10,2); v_begin_odometer NUMERIC(10,2); v_end_odometer NUMERIC(10,2); v_odometer1 NUMERIC(10,2); v_odometer2 NUMERIC(10,2); BEGIN
SELECT s.route_id,spb.measure,spe.measure INTO v_route_id,v_report_measure_begin,v_report_measure_end FROM pods.series s JOIN pods.station_point spb ON s.series_id = spb.series_id JOIN pods.event_range er ON er.station_id_begin = spb.station_id JOIN pods.station_point spe ON er.station_id_end = spe.station_id JOIN pods.ili_inspection_range ir ON ir.event_id = er.event_id WHERE ir.ili_inspection_id = {INSPECTION_ID};
IF v_route_id IS NULL OR v_route_id = 0 THEN RETURN; END IF;
FOR i IN ( WITH row_generator
AS (SELECT v_report_measure_begin +(series.a-1) * {REGULAR_INTERVALS_DISTANCE} measure FROM generate_series(1, (v_report_measure_end-v_report_measure_begin)/{REGULAR_INTERVALS_DISTANCE} + 1) as series(a)), base_sp AS (SELECT measure ,station_id ,LEAD(measure) OVER(ORDER BY measure) n_measure ,LEAD(station_id) OVER (ORDER BY measure) n_station_id ,sp.series ,sp.station ,sp.series_id ,r.route_id ,r.line_id FROM pods.station_point sp JOIN pods.series s ON s.series_id = sp.series_id JOIN pods.route r ON r.route_id = s.route_id JOIN pods.coordinate c ON c.location_id = sp.location_id WHERE r.route_id = v_route_id AND sp.type_cl IN ('ST_POINT_TYPE_01','ST_POINT_TYPE_02','ST_POINT_TYPE_03')) (SELECT sp.line_id, sp.route_id, sp.series_id, sp.series, sp.station, s.measure FROM row_generator s JOIN base_sp sp ON s.measure>=sp.measure AND s.measure<sp.n_measure UNION ALL SELECT s.line_id, s.route_id, s.series_id, MAX(sp.series) series, MAX(sp.station) station, MAX(sp.measure) measure FROM pods.series s JOIN pods.station_point sp ON sp.series_id = s.series_id JOIN PODS.coordinate c ON c.location_id = sp.location_id WHERE s.route_id = v_route_id AND sp.type_cl IN ('ST_POINT_TYPE_01','ST_POINT_TYPE_02','ST_POINT_TYPE_03') AND sp.measure BETWEEN v_report_measure_begin AND v_report_measure_end GROUP BY s.line_id, s.route_id, s.series_id, sp.series) ORDER BY MEASURE) LOOP
v_station_id_begin := v_station_id_end; v_begin_odometer := v_end_odometer;
SELECT count(*) INTO v_event_like_this FROM pods.event_range er JOIN pods.station_point spe ON er.station_id_end = spe.station_id JOIN pods.series s ON s.series_id = spe.series_id WHERE feature_id = 'INTERVAL_REGULAR' AND er.CURRENT_INDICATOR_LF = 'Y' AND spe.measure = i.measure AND spe.type_cl = 'ST_POINT_TYPE_04' AND length = {REGULAR_INTERVALS_DISTANCE} AND s.route_id = v_route_id AND EXISTS (SELECT 1 FROM pods.sto_ili_inspection_calc ic WHERE ic.event_id = er.event_id AND ic.ili_inspection_id = {INSPECTION_ID});
IF v_event_like_this = 0 THEN
/* необходимо интерполировать значения с соседних пикетов*/
SELECT l.srv_district_gcl ,l.depth_of_cover ,c.x_coord ,c.y_coord ,c.z_coord ,sp.measure ,sp.station_id INTO v_srv_district_gcl ,v_depth_of_cover1 ,v_X1 ,v_Y1 ,v_Z1 ,v_measure1 ,v_station_id1 FROM pods.station_point sp JOIN pods.location l ON l.location_id = sp.location_id JOIN pods.coordinate c ON c.location_id = sp.location_id WHERE sp.route_id=i.route_id and sp.measure = (SELECT max(sp2.measure) FROM pods.station_point sp2 WHERE sp2.route_id=v_route_id AND sp2.measure <=i.measure) limit 1;
SELECT l.depth_of_cover ,c.x_coord ,c.y_coord ,c.z_coord ,sp.measure ,sp.station_id INTO v_depth_of_cover2 ,v_X2 ,v_Y2 ,v_Z2 ,v_measure2 ,v_station_id2 FROM pods.station_point sp JOIN pods.location l ON l.location_id = sp.location_id JOIN pods.coordinate c ON c.location_id = sp.location_id WHERE sp.route_id=i.route_id and sp.measure = (SELECT min(sp2.measure) FROM pods.station_point sp2 WHERE sp2.route_id=v_route_id AND sp2.measure >=i.measure) limit 1;
IF abs(v_measure2 - v_measure1)>{REGULAR_INTERVALS_DISTANCE}*0.1 THEN v_depth_of_cover := v_depth_of_cover1+(i.measure-v_measure1)*(v_depth_of_cover2-v_depth_of_cover1)/(v_measure2-v_measure1); v_X := v_X1+(i.measure-v_measure1)*(v_X2-v_X1)/(v_measure2-v_measure1); v_Y := v_Y1+(i.measure-v_measure1)*(v_Y2-v_Y1)/(v_measure2-v_measure1); v_Z := v_Z1+(i.measure-v_measure1)*(v_Z2-v_Z1)/(v_measure2-v_measure1); INSERT INTO pods.location (srv_district_gcl,depth_of_cover) VALUES (v_srv_district_gcl,v_depth_of_cover) RETURNING location_id INTO v_location_id;
INSERT INTO pods.station_point (line_id,route_id,series_id,series,location_id,station,measure,type_cl) VALUES (i.line_id,i.route_id,i.series_id,i.series,v_location_id,i.station,i.measure,'ST_POINT_TYPE_04') RETURNING station_id INTO v_station_id_end;
INSERT INTO pods.coordinate(coordinate_source_id, location_id, x_coord, y_coord, z_coord ,type_cl, horiz_indicator_lf, vert_indicator_lf, current_indicator_lf ,effective_from_date, accuracy_cl, create_date, pods_user) VALUES (9,v_location_id,v_X,v_Y,v_Z,'ASG','Y', 'Y', 'Y',current_timestamp, 'MEDIUM', current_timestamp, '{config.main.PODS_USER}'); ELSE v_station_id_end:=v_station_id1; END IF;
ELSE SELECT MIN(spe.station_id) INTO v_station_id_end FROM pods.event_range er JOIN pods.station_point spe ON er.station_id_end = spe.station_id JOIN pods.series s ON s.series_id = spe.series_id WHERE feature_id = 'INTERVAL_REGULAR' AND er.CURRENT_INDICATOR_LF = 'Y' AND spe.measure = i.measure AND spe.type_cl = 'ST_POINT_TYPE_04' AND length = {REGULAR_INTERVALS_DISTANCE} AND s.route_id = v_route_id AND EXISTS (SELECT 1 FROM pods.sto_ili_inspection_calc ic WHERE ic.event_id = er.event_id AND ic.ili_inspection_id = {INSPECTION_ID}); END IF;
/* Получена точка на трубе, создана или найдена, не важно, необходимо получить значение одометра в этом месте*/ BEGIN SELECT absolute_odometer,measure INTO v_odometer1,v_measure1 FROM (SELECT ROW_NUMBER() OVER (ORDER BY sp1.measure DESC,d.absolute_odometer ASC) RWN /* mssql compatibility*/ ,d.absolute_odometer ,sp1.measure FROM pods.station_point sp1 JOIN pods.station_point spb USING (series_id) JOIN pods.event_range er ON sp1.station_id = er.station_id_begin JOIN pods.ili_data d USING (event_id) WHERE spb.station_id = v_station_id_end AND sp1.measure<=spb.measure AND er.current_indicator_lf = 'Y' AND d.ili_inspection_id = {INSPECTION_ID}) d WHERE rwn = 1; EXCEPTION WHEN NO_DATA_FOUND THEN /* не нашлось ничего вокруг, попробую экстраполировать */ v_odometer1 := -1e6; v_measure1 := -1e6; END;
BEGIN SELECT absolute_odometer,measure INTO v_odometer2,v_measure2 FROM (SELECT ROW_NUMBER() OVER (ORDER BY sp1.measure ASC,d.absolute_odometer DESC) RWN /* mssql compatibility*/ ,d.absolute_odometer ,sp1.measure FROM pods.station_point sp1 JOIN pods.station_point spb USING (series_id) JOIN pods.event_range er ON sp1.station_id = er.station_id_begin JOIN pods.ili_data d USING (event_id) WHERE spb.station_id = v_station_id_end AND sp1.measure>= spb.measure AND er.current_indicator_lf = 'Y' AND d.ili_inspection_id = {INSPECTION_ID}) d WHERE rwn = 1; EXCEPTION WHEN NO_DATA_FOUND THEN /* не нашлось ничего вокруг, попробую экстраполировать */ v_odometer2 := 1e6; v_measure2 := 1e6; END;
IF v_measure2 != v_measure1 THEN v_end_odometer := v_odometer1+(i.measure-v_measure1)*(v_odometer2-v_odometer1)/(v_measure2-v_measure1); ELSE v_end_odometer := v_odometer1; END IF;
/* Если первый проход то пропускаем итерацию цикла */ IF v_station_id_begin IS NOT NULL THEN INSERT INTO pods.event_range(feature_id,station_id_begin,station_id_end,pods_user,create_date,effective_from_date,current_indicator_lf,positioning_type_cl,length) VALUES ('INTERVAL_REGULAR',v_station_id_begin,v_station_id_end,'{config.main.PODS_USER}', current_timestamp, current_timestamp, 'Y', 'UNKNOWN',{REGULAR_INTERVALS_DISTANCE}) RETURNING event_id INTO v_event_id; INSERT INTO pods.sto_ili_inspection_calc (EVENT_ID,ILI_INSPECTION_ID,TYPE_CL,BEGIN_ODOMETER,END_ODOMETER) VALUES (v_event_id,{INSPECTION_ID},'Оценка состояния по регулярным участкам ({REGULAR_INTERVALS_DISTANCE}m)',v_begin_odometer,v_end_odometer);
END IF;
END LOOP; EXCEPTION WHEN OTHERS THEN RAISE EXCEPTION ' произошла ошибка: % код %',SQLERRM, SQLSTATE ; END $$;

			DO $$
			DECLARE
			RETURNid integer;
			BEGIN
 			INSERT INTO pods.event_range(FEATURE_ID,STATION_ID_BEGIN,STATION_ID_END,PODS_USER,CREATE_DATE,VALIDITY_TOLERANCE,EFFECTIVE_FROM_DATE,CURRENT_INDICATOR_LF,POSITIONING_TYPE_CL) 
 			VALUES ('PI_CIS_READING', 0, 0, '{config.main.PODS_USER}', current_timestamp, 0, current_timestamp, 'Y', 'UNKNOWN') RETURNING EVENT_ID INTO RETURNid; 
 			INSERT INTO pods.pi_cis_reading(EVENT_ID, PS_ON, PS_OFF, DC_POTENTIAL_VOLTS, GRAD_ON, GRAD_OFF, D_GRAD, DD_GRAD, DESCRIPTION, INSPECTION_DATE, PI_CP_EVENT_ID, COMMENTS, SOURCE_GCL)
 			VALUES (RETURNid, {U_вкл}, {U_откл}, {min_пот}, {G_вкл}, {G_откл}, {dG}, {ddG}, SUBSTR('{COMM}',1,50), TO_DATE('{xpath_view.REPORT_VIEW.date:dd.MM.yyyy}','DD.MM.YYYY'), {db_command.CMD.EVENT_ID}, trim('{DESCR}')||';'||trim('{COORD_B}')||';'||trim('{COORD_L}', '{xpath_view.REPORT_VIEW.source}');
 			IF '{COMM}' LIKE 'КИП%' AND :DESCR IS NOT NULL THEN
 			INSERT INTO pods.event_range(FEATURE_ID,STATION_ID_BEGIN,STATION_ID_END,PODS_USER,CREATE_DATE,VALIDITY_TOLERANCE,EFFECTIVE_FROM_DATE,CURRENT_INDICATOR_LF,POSITIONING_TYPE_CL) 
 			VALUES ('PI_CP_READING', 0, 0, '{config.main.PODS_USER}', current_timestamp, 0, current_timestamp, 'Y', 'UNKNOWN') RETURNING EVENT_ID INTO RETURNid; 
 			INSERT INTO pods.pi_cp_reading(EVENT_ID, TYPE_CL, PS_ON, PS_OFF, RESISTANCE_READING, POTENTIAL_READING, GRAD_ON, GRAD_OFF, D_GRAD, DD_GRAD, DESCRIPTION, INSPECTION_DATE, PI_CP_EVENT_ID, REF_TEST_LEAD_EVENT_ID, COMMENTS, SOURCE_GCL)
 			VALUES (RETURNid, 'UNKNOWN', {U_вкл}, {U_откл}, {R/1000}, {min_пот}, {G_вкл}, {G_откл}, {dG}, {ddG}, SUBSTR('{COMM}',1,50), TO_DATE('{xpath_view.REPORT_VIEW.date:dd.MM.yyyy}','DD.MM.YYYY'), {db_command.CMD.EVENT_ID}, {REF_EVENT_ID}, trim('{DESCR}')||';'||trim('{COORD_B}')||';'||trim('{COORD_L}', '{xpath_view.REPORT_VIEW.source}');
 			END IF;
 			END $$;

			DO $$
			DECLARE RETURNid integer;
			BEGIN
 			IF '{DESCR}' IS NOT NULL THEN
 			INSERT INTO pods.event_range(FEATURE_ID,STATION_ID_BEGIN,STATION_ID_END,PODS_USER,CREATE_DATE,VALIDITY_TOLERANCE,EFFECTIVE_FROM_DATE,CURRENT_INDICATOR_LF,POSITIONING_TYPE_CL) 
 			VALUES ('PI_CP_READING', 0, 0, '{config.main.PODS_USER}', current_timestamp, 0, current_timestamp, 'Y', 'UNKNOWN') RETURNING EVENT_ID INTO RETURNid; 
 			INSERT INTO pods.pi_cp_reading(EVENT_ID, TYPE_CL, PS_ON, PS_OFF, RESISTANCE_READING, POTENTIAL_READING, GRAD_ON, GRAD_OFF, D_GRAD, DD_GRAD, DESCRIPTION, INSPECTION_DATE, PI_CP_EVENT_ID, REF_TEST_LEAD_EVENT_ID, COMMENTS, SOURCE_GCL)
 			VALUES (RETURNid, 'UNKNOWN', {U_вкл}, {U_откл}, {R/1000}, {min_пот}, {G_вкл}, {G_откл}, {dG}, {ddG}, SUBSTR(coalesce2('{NUMKIP}','КИП '||'{NUMKIP}'||';','')||'{COMM}',1,50), TO_DATE('{xpath_view.REPORT_VIEW.date:dd.MM.yyyy}','DD.MM.YYYY'), {db_command.CMD.EVENT_ID}, {REF_EVENT_ID}, trim('{DESCR}')||';'||trim('{COORD_B}')||';'||trim('{COORD_L}'), '{xpath_view.REPORT_VIEW.source}');
 			END IF;
 			END $$;

			DO $$
			DECLARE
			RETURNid integer;
			BEGIN 
			INSERT INTO pods.event_range(FEATURE_ID,STATION_ID_BEGIN,STATION_ID_END,PODS_USER,CREATE_DATE,VALIDITY_TOLERANCE,EFFECTIVE_FROM_DATE,CURRENT_INDICATOR_LF,POSITIONING_TYPE_CL) 
 			VALUES ('PI_CP_INSPECTION', 0, 0, '{config.main.PODS_USER}', current_timestamp, 0, current_timestamp, 'Y', 'UNKNOWN') RETURNING EVENT_ID INTO RETURNid;
 			INSERT INTO pods.pi_cp_inspection(EVENT_ID, TYPE_CL, DESCRIPTION, COMMENTS, INSPECTION_DATE, CRITERIA_CL, RESULT_CL, TOOL_CL, SOURCE_GCL, NOMINAL_SPACING, PI_EVENT_ID) 
 			VALUES ({EVENT_ID}, '{xpath_view.REPORT_VIEW.type}', '{xpath_view.REPORT_VIEW.descr}', '{xpath_view.REPORT_VIEW.km_start}-{xpath_view.REPORT_VIEW.km_end}', TO_DATE('{xpath_view.REPORT_VIEW.date:dd.MM.yyyy}','DD.MM.YYYY'), '{xpath_view.REPORT_VIEW.criteria}', '{xpath_view.REPORT_VIEW.result}', '{xpath_view.REPORT_VIEW.tool}', '{xpath_view.REPORT_VIEW.source}', '{xpath_view.REPORT_VIEW.nominal_spacing}', -90) ; 
 			END $$;

DO $$
BEGIN INSERT INTO pods.event_range (feature_id,station_id_begin,station_id_end,pods_user,create_date,effective_from_date,current_indicator_lf) SELECT DISTINCT 'SRV_DISTR_BND' ,FIRST_VALUE (sp.station_id) OVER (PARTITION BY l.srv_district_gcl ORDER BY r.sequence,sp.measure) station_id_begin ,FIRST_VALUE (sp.station_id) OVER (PARTITION BY l.srv_district_gcl ORDER BY r.sequence DESC,sp.measure DESC) station_id_end ,'{config.main.PODS_USER}',current_timestamp,current_timestamp,'Y' FROM pods.line ll JOIN pods.route r ON r.line_id = ll.line_id JOIN pods.station_point sp ON sp.route_id = r.route_id JOIN pods.location l ON l.location_id = sp.location_id WHERE ll.line_id = {P_LINE_ID} AND ((r.type_cl = 'ROUTE_TYPE_04' AND ll.type_cl = 'LINE_TYPE_04') OR (r.type_cl = 'ROUTE_TYPE_12' AND ll.type_cl = 'LINE_TYPE_03') OR (r.type_cl = 'ROUTE_TYPE_10' AND ll.type_cl = 'LINE_TYPE_01')) AND l.srv_district_gcl is not null AND sp.type_cl IN ('ST_POINT_TYPE_01', 'ST_POINT_TYPE_02', 'ST_POINT_TYPE_03') AND r.route_id != 0 AND l.location_id != 0;
INSERT INTO pods.srv_district_boundary (event_id,srv_district_id,description,source_gcl) SELECT e.event_id,l.srv_district_gcl,ln.description||' ('||spb.station ||'-' || spe.station ||')' description,'NGG' FROM pods.event_range e JOIN pods.station_point spb ON spb.station_id = e.station_id_begin JOIN pods.station_point spe ON spe.station_id = e.station_id_end JOIN pods.location l ON l.location_id = spb.location_id JOIN pods.line ln ON ln.line_id = spb.line_id WHERE e.feature_id = 'SRV_DISTR_BND' and e.current_indicator_lf = 'Y' AND spb.line_id = {P_LINE_ID}; END $$;

			DO $$

			DECLARE
 			group_id NUMERIC(16,0);
 			exists_id NUMERIC(16,0);
 			BEGIN
 			IF {EVENT_ID} IS NULL THEN
 			return;
 			END IF;

			 SELECT MIN(event_group_id)
 			INTO group_id
 			FROM pods.event_group_cross_ref WHERE event_id = {EVENT_ID} AND description = '1';

 			IF group_id IS NULL THEN
 			INSERT INTO pods.event_group(GROUP_NAME) VALUES('{GROUP_NAME}') RETURNING event_group_id INTO group_id;
 			INSERT INTO pods.event_group_cross_ref(EVENT_ID, EVENT_GROUP_ID, DESCRIPTION) VALUES({EVENT_ID}, group_id, '1');
 			END IF;

 			SELECT MIN(event_group_id)
 			INTO exists_id
 			FROM pods.event_group_cross_ref 
 			WHERE event_group_id = group_id AND event_id = {ID} AND description = '0';

 			IF exists_id IS NULL THEN
 			INSERT INTO pods.event_group_cross_ref(EVENT_ID, EVENT_GROUP_ID, DESCRIPTION) 
 			VALUES({ID}, group_id, '0');
 			END IF;
 			END $$;

			DO $$

			DECLARE
 			group_id NUMERIC(16,0);
 			exists_id NUMERIC(16,0);
 			BEGIN
 			IF {EVENT_ID} IS NULL THEN
 			return;
 			END IF;

 			SELECT MIN(event_group_id)
 			INTO group_id
 			FROM pods.event_group_cross_ref WHERE event_id = {EVENT_ID} AND description = '1';

 			IF group_id IS NULL THEN
 			INSERT INTO pods.event_group(GROUP_NAME) VALUES('{GROUP_NAME}') RETURNING event_group_id INTO group_id;
 			INSERT INTO pods.event_group_cross_ref(EVENT_ID, EVENT_GROUP_ID, DESCRIPTION) VALUES({EVENT_ID}, group_id, '1');
 			END IF;

 			SELECT MIN(event_group_id)
 			INTO exists_id
 			FROM pods.event_group_cross_ref 
 			WHERE event_group_id = group_id AND event_id = {EVENT_ID} AND description = '0';

 			IF exists_id IS NULL THEN
 			INSERT INTO pods.event_group_cross_ref(EVENT_ID, EVENT_GROUP_ID, DESCRIPTION) 
 			VALUES({ID}, group_id, '0');
 			END IF;
 			END $$;

DO $$
DECLARE i RECORD; v_station_id_begin NUMERIC; v_station_id_end NUMERIC; v_station_measure_begin NUMERIC; v_station_measure_end NUMERIC; BEGIN FOR i IN (WITH structural_elements AS (SELECT er.station_id_begin station_id,spb.measure FROM pods.event_range er JOIN pods.station_point spb ON spb.station_id = er.station_id_begin JOIN pods.series s ON s.series_id = spb.series_id WHERE er.event_id in ({CONSTRUCTION_ELEMENT_QUERY}) AND s.route_id = {ROUTE_ID} UNION ALL SELECT DISTINCT FIRST_VALUE(spx.station_id) OVER (PARTITION BY er.event_id ORDER BY ABS(spx.measure - (spb.measure + (spe.measure - spb.measure)/2 ))) station_id ,FIRST_VALUE(spx.measure) OVER (PARTITION BY er.event_id ORDER BY ABS(spx.measure - (spb.measure + (spe.measure - spb.measure)/2 ))) measure FROM pods.event_range er JOIN pods.station_point spb ON spb.station_id = er.station_id_begin JOIN pods.series s ON s.series_id = spb.series_id JOIN pods.station_point spe ON spe.station_id = er.station_id_end JOIN pods.station_point spX ON s.series_id = spX.series_id AND spX.measure BETWEEN spb.measure AND spe.measure WHERE er.event_id in ({CONSTRUCTION_ELEMENT_QUERY}) AND s.route_id = {ROUTE_ID} AND er.station_id_begin != er.station_id_end), limits_station AS ( SELECT MIN(measure) first_measure ,MAX(measure) last_measure ,(select station_id from pods.station_point ss where ss.route_id= {ROUTE_ID} and ss.measure=(select min(measure) from pods.station_point sss where sss.route_id= {ROUTE_ID}) limit 1) first_station_id ,(select station_id from pods.station_point ss where ss.route_id= {ROUTE_ID} and ss.measure=(select max(measure) from pods.station_point sss where sss.route_id= {ROUTE_ID}) limit 1) last_station_id FROM pods.station_point sp JOIN pods.series s ON s.series_id = sp.series_id WHERE s.route_id = {ROUTE_ID}), combained_junctions AS (SELECT first_station_id station_id,first_measure measure FROM limits_station ls1 WHERE NOT EXISTS (SELECT 1 FROM structural_elements se WHERE ls1.first_measure = se.measure) UNION ALL SELECT station_id,measure FROM structural_elements UNION ALL SELECT last_station_id station_id,last_measure FROM limits_station ls2 WHERE NOT EXISTS (SELECT 1 FROM structural_elements se WHERE ls2.last_measure = se.measure)) SELECT station_id,measure FROM combained_junctions ORDER BY measure) LOOP v_station_id_end := i.station_id; v_station_measure_end := i.measure;
IF v_station_id_end IS NOT NULL AND v_station_id_begin IS NOT NULL THEN INSERT INTO pods.event_range(feature_id,station_id_begin,station_id_end,pods_user,create_date,effective_from_date,current_indicator_lf,positioning_type_cl,length) VALUES ('INTERVAL_PER_OBJ',v_station_id_begin,v_station_id_end,'{config.main.PODS_USER}', current_timestamp, current_timestamp, 'Y', 'UNKNOWN',ABS(v_station_measure_end-v_station_measure_begin)); END IF; v_station_id_begin := v_station_id_end; v_station_measure_begin := v_station_measure_end;
END LOOP; END $$;

	DO $$

                 DECLARE
                 i RECORD;
                  v_station_id_begin NUMERIC;
                  v_station_id_end NUMERIC;
                  v_location_id NUMERIC;
                  v_srv_district_gcl NUMERIC;
                  v_depth_of_cover NUMERIC;
                  v_X NUMERIC;
                  v_Y NUMERIC;
                  v_Z NUMERIC;

                  v_depth_of_cover1 NUMERIC;
                  v_X1 NUMERIC;
                  v_Y1 NUMERIC;
                  v_Z1 NUMERIC;
                  v_measure1 NUMERIC;
		  v_station_id1 NUMERIC;

                  v_depth_of_cover2 NUMERIC;
                  v_X2 NUMERIC;
                  v_Y2 NUMERIC;
                  v_Z2 NUMERIC;
                  v_measure2 NUMERIC;
                  v_min_measure NUMERIC;
                  v_max_measure NUMERIC;
		  v_station_id2 NUMERIC;

                 BEGIN

                  SELECT MIN(sp.measure) min_measure
                  ,MAX(sp.measure) max_measure
  		  into v_min_measure,v_max_measure
                  FROM pods.series s
                  JOIN pods.station_point sp 
                  ON sp.series_id = s.series_id
                  WHERE s.route_id = {ROUTE_ID};

                  FOR i IN (WITH 
                  row_generator
                  AS (
                  SELECT v_min_measure+(series.a-1) * {REGULAR_INTERVALS_DISTANCE} measure FROM generate_series 
                   (1, ((SELECT MAX(sp.measure) - MIN(sp.measure)
                  FROM pods.series s
                  JOIN pods.station_point sp 
                  ON sp.series_id = s.series_id
                  WHERE s.route_id = {ROUTE_ID}))/{REGULAR_INTERVALS_DISTANCE} + 1) as series(a)),
                  base_sp
                  AS (SELECT measure
                  ,station_id
                  ,LEAD(measure) OVER(ORDER BY measure) n_measure
                  ,LEAD(station_id) OVER (ORDER BY measure) n_station_id 
                  FROM pods.station_point sp
                  JOIN pods.series s
                  ON s.series_id = sp.series_id
                  WHERE s.route_id = {ROUTE_ID}
                  AND type_cl IN ('ST_POINT_TYPE_01','ST_POINT_TYPE_02','ST_POINT_TYPE_03'))
                  (SELECT sp1.line_id, sp1.route_id, sp1.series_id, sp1.series, sp1.station, s.measure
                  FROM row_generator s
                  JOIN base_sp sp 
                  ON s.measure>=sp.measure 
                  AND s.measure<sp.n_measure
                  JOIN PODS.STATION_POINT sp1 
                  ON sp1.station_id = sp.station_id
                  JOIN pods.coordinate c1 
                  ON c1.location_id = sp1.location_id
                  JOIN PODS.STATION_POINT sp2 
                  ON sp2.station_id = sp.n_station_id
                  JOIN pods.coordinate c2 
                  ON c2.location_id = sp2.location_id
                  UNION ALL
                  SELECT s.line_id, s.route_id, s.series_id, sp.series, MAX(sp.station) station, MAX(sp.measure) measure
                  FROM pods.series s
                  JOIN pods.station_point sp
                  ON sp.series_id = s.series_id
                  WHERE s.route_id = {ROUTE_ID}
                  GROUP BY s.line_id, s.route_id, s.series_id, sp.series)
                  ORDER BY MEASURE)
                  LOOP

                  v_station_id_begin := v_station_id_end;

   			/* необходимо интерполировать значения с соседних пикетов*/

                 SELECT l.srv_district_gcl
 			,l.depth_of_cover
 			,c.x_coord
 			,c.y_coord
 			,c.z_coord
 			,sp.measure
			,sp.station_id
 			INTO v_srv_district_gcl
 			,v_depth_of_cover1 
 			,v_X1 
 			,v_Y1 
 			,v_Z1 
 			,v_measure1
			,v_station_id1			 
 			FROM pods.station_point sp
 			JOIN pods.location l
 			ON l.location_id = sp.location_id
 			JOIN pods.coordinate c 
 			ON c.location_id = sp.location_id 
 			WHERE sp.route_id={ROUTE_ID}
                         and sp.measure = (SELECT max(sp2.measure) 
                        FROM pods.station_point sp2
                        WHERE sp2.route_id={ROUTE_ID}
                        AND sp2.measure <=i.measure)
                        limit 1;

 			SELECT l.depth_of_cover
 			,c.x_coord
 			,c.y_coord
 			,c.z_coord
 			,sp.measure
			,sp.station_id
 			INTO v_depth_of_cover2 
 			,v_X2 
 			,v_Y2 
 			,v_Z2 
 			,v_measure2 
			,v_station_id2
 			FROM pods.station_point sp
 			JOIN pods.location l
 			ON l.location_id = sp.location_id
 			JOIN pods.coordinate c 
 			ON c.location_id = sp.location_id 
 			WHERE sp.route_id={ROUTE_ID}
                         and sp.measure = (SELECT min(sp2.measure) 
                        FROM pods.station_point sp2
                        WHERE sp2.route_id={ROUTE_ID}
                        AND sp2.measure >=i.measure)
                        limit 1; 

                  IF abs(v_measure2 - v_measure1)>{REGULAR_INTERVALS_DISTANCE}*0.1
                  THEN 
                  v_depth_of_cover := v_depth_of_cover1+(i.measure-v_measure1)*(v_depth_of_cover2-v_depth_of_cover1)/(v_measure2-v_measure1);
                  v_X := v_X1+(i.measure-v_measure1)*(v_X2-v_X1)/(v_measure2-v_measure1); 
                  v_Y := v_Y1+(i.measure-v_measure1)*(v_Y2-v_Y1)/(v_measure2-v_measure1); 
                  v_Z := v_Z1+(i.measure-v_measure1)*(v_Z2-v_Z1)/(v_measure2-v_measure1);
                  INSERT INTO pods.location (srv_district_gcl,depth_of_cover) VALUES (v_srv_district_gcl,v_depth_of_cover)
                  RETURNING location_id INTO v_location_id;

                  INSERT INTO pods.station_point (line_id,route_id,series_id,series,location_id,station,measure,type_cl)
                  VALUES (i.line_id,i.route_id,i.series_id,i.series,v_location_id,i.station,i.measure,'ST_POINT_TYPE_04')
                  RETURNING station_id INTO v_station_id_end; 

                  INSERT INTO pods.coordinate(coordinate_source_id, location_id, x_coord, y_coord, z_coord
                  ,type_cl, horiz_indicator_lf, vert_indicator_lf, current_indicator_lf
                  ,effective_from_date, accuracy_cl, create_date, pods_user)
                  VALUES (9,v_location_id,v_X,v_Y,v_Z,'ASG','Y', 'Y', 'Y',current_timestamp, 'MEDIUM', current_timestamp, '{config.main.PODS_USER}');
                  ELSE
                  v_station_id_end:=v_station_id1;
                  END IF; 




 		 /* Если первый проход то пропускаем итерацию цикла */
                  IF v_station_id_begin IS NOT NULL
                  THEN
                  INSERT INTO pods.event_range(feature_id,station_id_begin,station_id_end,pods_user,create_date,effective_from_date,current_indicator_lf,positioning_type_cl,length) 
                  VALUES ('INTERVAL_REGULAR',v_station_id_begin,v_station_id_end,'{config.main.PODS_USER}', current_timestamp, current_timestamp, 'Y', 'UNKNOWN',{REGULAR_INTERVALS_DISTANCE});

                  END IF;

                  END LOOP; 
                 END $$;

			DO $$

			BEGIN
 			IF {MEASURE_START} IS NOT NULL THEN
 			UPDATE pods.station_point 
 			SET MEASURE = {MEASURE_START}, STATION = {STATION_START}, SERIES_ID = {SERIES_ID_START}, TYPE_CL = 'ST_POINT_TYPE_05',
 			ROUTE_ID = {ROUTE_ID_START}, LINE_ID = {LINE_ID_START}
 			WHERE station_id = {STATION_ID};

 			UPDATE pods.event_range 
 			SET validity_tolerance = {DISTANCE}
 			WHERE event_id = {EVENT_ID};
 			END IF;
 			END $$;

			DO $$

			BEGIN
 			IF {LINE_COORD_START} IS NOT NULL THEN
 			UPDATE pods.station_point 
 			SET MEASURE = {LINE_COORD_START}, STATION = {KM_START}, SERIES_ID = {AXE_ID}, TYPE_CL = 'ST_POINT_TYPE_05',
 			ROUTE_ID = (SELECT ROUTE_ID FROM PODS.SERIES s WHERE s.SERIES_ID = {AXE_ID}),
 			LINE_ID = (SELECT LINE_ID FROM PODS.SERIES s WHERE s.SERIES_ID = {AXE_ID})
 			WHERE station_id = {STATION_ID};

 			UPDATE pods.event_range 
 			SET validity_tolerance = {DISTANCE}
 			WHERE event_id = {EVENT_ID};
 			END IF;
 			END $$;

			DO $$

			BEGIN
 			IF {LINE_COORD_START} IS NOT NULL THEN
 			UPDATE pods.station_point 
 			SET MEASURE = {LINE_COORD_START}, STATION = {KM_START}, SERIES_ID = {AXE_ID}, TYPE_CL = 'ST_POINT_TYPE_05',
 			ROUTE_ID = (SELECT ROUTE_ID FROM PODS.SERIES s WHERE s.SERIES_ID = {AXE_ID}),
 			LINE_ID = (SELECT LINE_ID FROM PODS.SERIES s WHERE s.SERIES_ID = {AXE_ID})
 			WHERE station_id = {STATION_ID};

 			UPDATE pods.event_range 
 			SET validity_tolerance = {DISTANCE}
 			WHERE event_id = {EVENT_ID};
 			END IF;
 			END $$;

DO $$
BEGIN UPDATE pods.event_range SET CURRENT_INDICATOR_LF = 'N' WHERE event_id IN (SELECT c.event_id FROM pods.offline_cross_ref c JOIN pods.event_range e ON e.event_id = c.event_id JOIN pods.offline_event oe ON oe.offline_id = c.offline_id JOIN pods.station_point sp ON sp.station_id = e.station_id_begin AND sp.line_id = {xpath_view.SEL_LINE.LINE_ID} WHERE oe.feature_id IN ('{call_method.RES_FEATURE_ID}') AND e.feature_id = 'OFF_CRSS_REF');
delete from pods.offline_cross_ref c WHERE event_id IN (SELECT c.event_id FROM pods.offline_cross_ref c JOIN pods.event_range e ON e.event_id = c.event_id JOIN pods.offline_event oe ON oe.offline_id = c.offline_id JOIN pods.station_point sp ON sp.station_id = e.station_id_begin AND sp.line_id = {xpath_view.SEL_LINE.LINE_ID} WHERE oe.feature_id IN ('{call_method.RES_FEATURE_ID}') AND e.feature_id = 'OFF_CRSS_REF');
END $$;

			DO $$

			DECLARE
 			loc_id_start numeric;
 			loc_id_end numeric;
 			sp_id_start numeric;
 			sp_id_end numeric;
 			e_id numeric;
 			BEGIN
 			IF {MEASURE_START} IS NOT NULL THEN
 			IF ABS({N_MEASURE_START}-{MEASURE_START})<{LINK_RADIUS} THEN
 			sp_id_start := {STATION_ID_START};
 			ELSE
 			INSERT INTO pods.location(SRV_DISTRICT_GCL, DEPTH_OF_COVER)
 			VALUES({SRV_DISTRICT_GCL_START}, {DEPTH_OF_COVER_START})
 			RETURNING LOCATION_ID INTO loc_id_start;

 			INSERT INTO pods.coordinate
 			(coordinate_source_id, type_cl, location_id, x_coord, y_coord, z_coord, horiz_indicator_lf, vert_indicator_lf,current_indicator_lf, effective_from_date, create_date, pods_user, accuracy_cl, validity_tolerance )
 			VALUES (9, 'ASG', loc_id_start, round({X_START},6), round({Y_START},6), round({Z_START},2), 'Y', (CASE WHEN {Z_START} is not null THEN 'Y' ELSE 'N' END), 'Y', current_timestamp, current_timestamp, '{config.main.PODS_USER}', 'MEDIUM', 0 );

 			INSERT INTO pods.station_point
			 				 (type_cl, location_id, line_id, series, station, series_id, route_id, measure, create_date, pods_user )
 			 			VALUES ('ST_POINT_TYPE_05', loc_id_start, {LINE_ID_START}, {SERIES_START}, {STATION_START}, {SERIES_ID_START}, {ROUTE_ID_START}, {MEASURE_START}, current_timestamp, '{config.main.PODS_USER}' )
 			RETURNING STATION_ID INTO sp_id_start;
 			END IF;

 			IF ABS({N_MEASURE_END}-{MEASURE_END})<{LINK_RADIUS} THEN
 			sp_id_end := {STATION_ID_END};
 			ELSE
 			INSERT INTO pods.location(SRV_DISTRICT_GCL, DEPTH_OF_COVER)
 			VALUES({SRV_DISTRICT_GCL_END}, {DEPTH_OF_COVER_END})
 			RETURNING LOCATION_ID INTO loc_id_end;

 			INSERT INTO pods.coordinate
 			(coordinate_source_id, type_cl, location_id, x_coord, y_coord, z_coord, horiz_indicator_lf, vert_indicator_lf,current_indicator_lf, effective_from_date, create_date, pods_user, accuracy_cl, validity_tolerance )
 			VALUES (9, 'ASG', loc_id_end, round({X_END},6), round({Y_END},6), round({Z_END},2), 'Y', (CASE WHEN {Z_START} is not null THEN 'Y' ELSE 'N' END), 'Y', current_timestamp, current_timestamp, '{config.main.PODS_USER}', 'MEDIUM', 0 );

 			INSERT INTO pods.station_point
 				 			(type_cl, location_id, line_id, series, station, series_id, route_id, measure, create_date, pods_user )
 			 			VALUES ('ST_POINT_TYPE_05', loc_id_end, {LINE_ID_END}, {SERIES_END}, {STATION_END}, {SERIES_ID_END}, {ROUTE_ID_END}, {MEASURE_END}, current_timestamp, '{config.main.PODS_USER}' )
 			RETURNING STATION_ID INTO sp_id_end;
 			END IF;

 			INSERT INTO PODS.EVENT_RANGE (FEATURE_ID,STATION_ID_BEGIN,STATION_ID_END,PODS_USER,CREATE_DATE,EFFECTIVE_FROM_DATE,CURRENT_INDICATOR_LF,POSITIONING_TYPE_CL)
 			VALUES('OFF_CRSS_REF',sp_id_start,sp_id_end,'{config.main.PODS_USER}',current_timestamp,current_timestamp, 'Y','UNKNOWN')
 			RETURNING event_id INTO e_id;

 			INSERT INTO PODS.OFFLINE_CROSS_REF(EVENT_ID, OFFLINE_ID, "OFFSET", TYPE_CL, DESCRIPTION, DIRECTION_GCL, SOURCE_GCL)
 			VALUES (e_id, {ID}, {DISTANCE}, '{OFFLINE_EVENT_TYPE_CL}','{OFFLINE_EVENT_TYPE_CL}', 'UNKNOWN', 'NGG');
 			END IF;
 			END $$;

DO $$
BEGIN UPDATE pods.event_range SET CURRENT_INDICATOR_LF = 'N' WHERE event_id IN (SELECT c.event_id FROM pods.offline_cross_ref c JOIN pods.event_range e ON e.event_id = c.event_id JOIN pods.offline_event oe ON oe.offline_id = c.offline_id JOIN pods.station_point sp ON sp.station_id = e.station_id_begin AND sp.route_id = {xpath_view.SEL_ROUTE.LINE_ID} WHERE oe.feature_id IN ('{call_method.RES_FEATURE_ID}') AND e.feature_id = 'OFF_CRSS_REF');
delete from pods.offline_cross_ref WHERE event_id IN (SELECT c.event_id FROM pods.offline_cross_ref c JOIN pods.event_range e ON e.event_id = c.event_id JOIN pods.offline_event oe ON oe.offline_id = c.offline_id JOIN pods.station_point sp ON sp.station_id = e.station_id_begin AND sp.route_id = {xpath_view.SEL_ROUTE.LINE_ID} WHERE oe.feature_id IN ('{call_method.RES_FEATURE_ID}') AND e.feature_id = 'OFF_CRSS_REF'); END $$;

			DO $$

			DECLARE
 			loc_id_start NUMERIC;
 			loc_id_end NUMERIC;
 			sp_id_start NUMERIC;
 			sp_id_end NUMERIC;
 			e_id NUMERIC;
 			BEGIN
 			IF {MEASURE_START} IS NOT NULL THEN
 			IF ABS({N_MEASURE_START}-{MEASURE_START})<{LINK_RADIUS} THEN
 			sp_id_start := {STATION_ID_START};
 			ELSE
 			INSERT INTO pods.location(SRV_DISTRICT_GCL, DEPTH_OF_COVER)
 			VALUES({SRV_DISTRICT_GCL_START}, {DEPTH_OF_COVER_START})
 			RETURNING LOCATION_ID INTO loc_id_start;

 			INSERT INTO pods.coordinate
 			(coordinate_source_id, type_cl, location_id, x_coord, y_coord, z_coord, horiz_indicator_lf, vert_indicator_lf,current_indicator_lf, effective_from_date, create_date, pods_user, accuracy_cl, validity_tolerance )
 			VALUES (9, 'ASG', loc_id_start, round({X_START},6), round({Y_START},6), round({Z_START},2), 'Y', coalesce2({Z_START},'Y','N'), 'Y', current_timestamp, current_timestamp, '{config.main.PODS_USER}', 'MEDIUM', 0 );

 			INSERT INTO pods.station_point
 				 			(type_cl, location_id, line_id, series, station, series_id, route_id, measure, create_date, pods_user )
 			 			VALUES ('ST_POINT_TYPE_05', loc_id_start, (select line_id from pods.route where route_id={LINE_ID_START}), {SERIES_START}, {STATION_START}, {SERIES_ID_START}, {ROUTE_ID_START}, {MEASURE_START}, current_timestamp, '{config.main.PODS_USER}' )
 			RETURNING STATION_ID INTO sp_id_start;
 			END IF;

 			IF ABS({N_MEASURE_END}-{MEASURE_END})<{LINK_RADIUS} THEN
 			sp_id_end := {STATION_ID_END};
 			ELSE
 			INSERT INTO pods.location(SRV_DISTRICT_GCL, DEPTH_OF_COVER)
 			VALUES({SRV_DISTRICT_GCL_END}, {DEPTH_OF_COVER_END})
 			RETURNING LOCATION_ID INTO loc_id_end;

 			INSERT INTO pods.coordinate
 			(coordinate_source_id, type_cl, location_id, x_coord, y_coord, z_coord, horiz_indicator_lf, vert_indicator_lf,current_indicator_lf, effective_from_date, create_date, pods_user, accuracy_cl, validity_tolerance )
 			VALUES (9, 'ASG', loc_id_end, round({X_END},6), round({Y_END},6), round({Z_END},2), 'Y', coalesce2({Z_END},'Y','N'), 'Y', current_timestamp, current_timestamp, '{config.main.PODS_USER}', 'MEDIUM', 0 );

 			INSERT INTO pods.station_point
 				 			(type_cl, location_id, line_id, series, station, series_id, route_id, measure, create_date, pods_user )
 			 			VALUES ('ST_POINT_TYPE_05', loc_id_end, (select line_id from pods.route where route_id={LINE_ID_END}), {SERIES_END}, {STATION_END}, {SERIES_ID_END}, {ROUTE_ID_END}, {MEASURE_END}, current_timestamp, '{config.main.PODS_USER}' )
 			RETURNING STATION_ID INTO sp_id_end;
 			END IF;

			 INSERT INTO PODS.EVENT_RANGE (FEATURE_ID,STATION_ID_BEGIN,STATION_ID_END,PODS_USER,CREATE_DATE,EFFECTIVE_FROM_DATE,CURRENT_INDICATOR_LF,POSITIONING_TYPE_CL)
 			VALUES('OFF_CRSS_REF',sp_id_start,sp_id_end,'{config.main.PODS_USER}',current_timestamp,current_timestamp, 'Y','UNKNOWN')
 			RETURNING event_id INTO e_id;

 			INSERT INTO PODS.OFFLINE_CROSS_REF(EVENT_ID, OFFLINE_ID, "OFFSET", TYPE_CL, DESCRIPTION, DIRECTION_GCL, SOURCE_GCL)
 			VALUES (e_id, {ID}, {DISTANCE}, '{OFFLINE_EVENT_TYPE_CL}','{OFFLINE_EVENT_TYPE_CL}', 'UNKNOWN', 'NGG');
 			END IF;
 			END $$;

			DO $$

			BEGIN
 			UPDATE pods.event_range SET CURRENT_INDICATOR_LF = 'N'
 			WHERE event_id IN
 			(SELECT c.event_id
 			FROM pods.offline_cross_ref c
 			WHERE c.offline_id IN ({OFFLINE_ID}));

 			DELETE FROM pods.offline_cross_ref
 			WHERE offline_id IN ({OFFLINE_ID});
 			END $$;

			DO $$

			DECLARE
 			loc_id_start NUMERIC(16,0);
 			loc_id_end NUMERIC(16,0);
 			sp_id_start NUMERIC(16,0);
 			sp_id_end NUMERIC(16,0);
 			e_id NUMERIC(16,0);
 			BEGIN
 			IF {MEASURE_START} IS NOT NULL THEN
 			IF ABS({N_MEASURE_START}-{MEASURE_START})<{LINK_RADIUS} THEN
 			sp_id_start := {STATION_ID_START};
 			ELSE
 			INSERT INTO pods.location(SRV_DISTRICT_GCL, DEPTH_OF_COVER)
 			VALUES({SRV_DISTRICT_GCL_START}, {DEPTH_OF_COVER_START})
 			RETURNING LOCATION_ID INTO loc_id_start;

 			INSERT INTO pods.coordinate
 			(coordinate_source_id, type_cl, location_id, x_coord, y_coord, z_coord, horiz_indicator_lf, vert_indicator_lf,current_indicator_lf, effective_from_date, create_date, pods_user, accuracy_cl, validity_tolerance )
 			VALUES (9, 'ASG', loc_id_start, round({X_START},6), round({Y_START},6), round({Z_START},2), 'Y', CASE WHEN {Z_START} IS NOT NULL THEN 'Y' ELSE 'N' END, 'Y', current_timestamp, current_timestamp, '{config.main.PODS_USER}', 'MEDIUM', 0 );

 			INSERT INTO pods.station_point
 				 			(type_cl, location_id, line_id, series, station, series_id, route_id, measure, create_date, pods_user )
 			 			VALUES ('ST_POINT_TYPE_05', loc_id_start, {LINE_ID_START}, {SERIES_START}, {STATION_START}, {SERIES_ID_START}, {ROUTE_ID_START}, {MEASURE_START}, current_timestamp, '{config.main.PODS_USER}' )
 			RETURNING STATION_ID INTO sp_id_start;
 			END IF;

 			IF ABS({N_MEASURE_END}-{MEASURE_END})<{LINK_RADIUS} THEN
 			sp_id_end := {STATION_ID_END};
 			ELSE
 			INSERT INTO pods.location(SRV_DISTRICT_GCL, DEPTH_OF_COVER)
 			VALUES({SRV_DISTRICT_GCL_END}, {DEPTH_OF_COVER_END})
 			RETURNING LOCATION_ID INTO loc_id_end;

 			INSERT INTO pods.coordinate
 			(coordinate_source_id, type_cl, location_id, x_coord, y_coord, z_coord, horiz_indicator_lf, vert_indicator_lf,current_indicator_lf, effective_from_date, create_date, pods_user, accuracy_cl, validity_tolerance )
 			VALUES (9, 'ASG', loc_id_end, round({X_END},6), round({Y_END},6), round({Z_END},2), 'Y', CASE WHEN {Z_END} IS NOT NULL THEN 'Y' ELSE 'N' END, 'Y', current_timestamp, current_timestamp, '{config.main.PODS_USER}', 'MEDIUM', 0 );

 			INSERT INTO pods.station_point
 				 			(type_cl, location_id, line_id, series, station, series_id, route_id, measure, create_date, pods_user )
 			 			VALUES ('ST_POINT_TYPE_05', loc_id_end, {LINE_ID_END}, {SERIES_END}, {STATION_END}, {SERIES_ID_END}, {ROUTE_ID_END}, {MEASURE_END}, current_timestamp, '{config.main.PODS_USER}' )
 			RETURNING STATION_ID INTO sp_id_end;
 			END IF;

 			INSERT INTO PODS.EVENT_RANGE (FEATURE_ID,STATION_ID_BEGIN,STATION_ID_END,PODS_USER,CREATE_DATE,EFFECTIVE_FROM_DATE,CURRENT_INDICATOR_LF,POSITIONING_TYPE_CL)
 			VALUES('OFF_CRSS_REF',sp_id_start,sp_id_end,'{config.main.PODS_USER}',current_timestamp,current_timestamp, 'Y','UNKNOWN')
 			RETURNING EVENT_ID INTO e_id;

 			INSERT INTO PODS.OFFLINE_CROSS_REF(EVENT_ID, OFFLINE_ID, "OFFSET", TYPE_CL, DIRECTION_GCL, SOURCE_GCL)
 			VALUES (e_id, {ID}, {DISTANCE}, '{OFFLINE_EVENT_TYPE}', 'UNKNOWN', 'NGG');
 			END IF;
 			END $$;

			DO $$

			DECLARE
 			km_start NUMERIC(10,2);
 			km_end NUMERIC(10,2);
 			lc_start NUMERIC(10,2);
 			lc_end NUMERIC(10,2);
 			BEGIN
 			DELETE FROM WEB50.PS_INDEX_OBJ WHERE KEY_VALUES = '{xpath_iterator.CUR_OBJ.@ID}';

 			SELECT sp1.measure+{xpath.CUR_PS.@s_fract}*(sp2.measure-sp1.measure)
 			,sp1.station+{xpath.CUR_PS.@s_fract}*(sp2.station-sp1.station)
 			INTO lc_start,km_start
 			FROM pods.station_point sp1, pods.station_point sp2
 			WHERE sp1.station_id = {xpath.CUR_PS.@ss_id} AND sp2.station_id = {xpath.CUR_PS.@se_id};

			 SELECT sp1.measure+{xpath.CUR_PS.@e_fract}*(sp2.measure-sp1.measure)
 			,sp1.station+{xpath.CUR_PS.@e_fract}*(sp2.station-sp1.station)
 			INTO lc_end,km_end
 			FROM pods.station_point sp1, pods.station_point sp2
 			WHERE sp1.station_id = {xpath.CUR_PS.@es_id} AND sp2.station_id = {xpath.CUR_PS.@ee_id};

 			INSERT INTO WEB50.PS_INDEX_OBJ(TEMPLATE_ID, PIPE_SYSTEM_ID, STATE_ID, KEY_FIELDS, KEY_VALUES, KM_START, KM_END, LINE_COORD_START, LINE_COORD_END, DISTANCE, GID, CUSER_ID) 
 			VALUES(1, {xpath.CUR_PS.@id}, 0, '', '{xpath_iterator.CUR_OBJ.@ID}', km_start, km_end, lc_start, lc_end, {xpath.CUR_PS.@dist}, NULL, 0);
 			END $$;

DO $$ BEGIN update pods.ili_data set weld_number=null where weld_number like '' and ili_inspection_id = {db_command.CMD.ILI_INSPECTION_ID};
update pods.ili_data d set us_weld_distance=d.absolute_odometer-d.us_weld_odometer ,ds_weld_distance=d.ds_weld_odometer - d.absolute_odometer where EXISTS (select 1 from pods.ili_data ddd JOIN pods.ili_data ww ON ww.ili_inspection_id = ddd.ili_inspection_id AND ddd.absolute_odometer>=ww.absolute_odometer AND ww.absolute_odometer>=ddd.absolute_odometer-100 WHERE ww.ili_inspection_id = {db_command.CMD.ILI_INSPECTION_ID} AND ww.anomaly_type_cl = 5001 AND ddd.ili_data_id = d.ili_data_id);
END $$;

DO $$ BEGIN
update pods.ili_data set weld_number=null where weld_number like '' and ili_inspection_id = {db_command_err.CMD.ILI_INSPECTION_ID};
update pods.ili_data d set us_weld_distance=d.absolute_odometer-d.us_weld_odometer ,ds_weld_distance=d.ds_weld_odometer - d.us_weld_odometer + d.absolute_odometer - d.us_weld_odometer where EXISTS (select 1 from pods.ili_data ddd JOIN pods.ili_data ww ON ww.ili_inspection_id = ddd.ili_inspection_id AND ddd.absolute_odometer>=ww.absolute_odometer AND ww.absolute_odometer>=ddd.absolute_odometer-100 WHERE ww.ili_inspection_id = {db_command_err.CMD.ILI_INSPECTION_ID} AND ddd.weld_number is null AND ww.anomaly_type_cl = 5001 AND ddd.ili_data_id = d.ili_data_id);
END $$;

DO $$ DECLARE RETURNiliid integer; RETURNeventid integer; v_pipe varchar(50) default 'Объект лежит не на оси трубопровода'; BEGIN select l.description from pods.line l, pods.route r where r.route_id = {ROUTE_ID} and l.line_id = r.line_id into v_pipe; INSERT INTO PODS.ILI_INSPECTION(BEGIN_DATE, END_DATE, START_ODOMETER, END_ODOMETER, TOOL_TYPE_CL, MODEL, SENSOR_GCL, SENSOR_SPACING_MIN, SENSOR_SPACING_MAX, MIN_TEMP, MAX_TEMP, AVG_TEMP, MIN_VELOCITY, MAX_VELOCITY, AVG_VELOCITY, SAMPLING_FREQUENCY, RESOLUTION, RATED_MAX_VELOCITY, RATED_MAX_WT, CLUSTER_RULE_CL, SOURCE_GCL, COMMENTS, TOOL_VENDOR_CL) VALUES (TO_DATE('{DATE}','DD.MM.YYYY'), TO_DATE('{DATE}','DD.MM.YYYY'), null::numeric, null::numeric, 'UNKNOWN', '{MODEL}', 'UNKNOWN', null::numeric, null::numeric, null::numeric, null::numeric, null::numeric, null::numeric, null::numeric, null::numeric, null::numeric, null::numeric, null::numeric, null::numeric, 'UNKNOWN', 'UNKNOWN', '{FORMAT}', '{COMPANY}') RETURNING ili_inspection_id INTO RETURNiliid;
IF {ROUTE_ID} IS NOT NULL or {ROUTE_ID}>0 THEN INSERT INTO pods.event_range(FEATURE_ID,STATION_ID_BEGIN,STATION_ID_END,PODS_USER,CREATE_DATE,EFFECTIVE_FROM_DATE,CURRENT_INDICATOR_LF,POSITIONING_TYPE_CL) VALUES ('ILI_INSP_RNG', (SELECT MIN(station_id) FROM pods.station_point sp WHERE sp.route_id = {ROUTE_ID} AND sp.measure = (SELECT MIN(spp.measure) FROM pods.station_point spp WHERE spp.route_id = {ROUTE_ID})), (SELECT MAX(station_id) FROM pods.station_point sp WHERE sp.route_id = {ROUTE_ID} AND sp.measure = (SELECT MAX(spp.measure) FROM pods.station_point spp WHERE spp.route_id = {ROUTE_ID})), '{config.main.PODS_USER}', current_timestamp, current_timestamp, 'Y', 'UNKNOWN') RETURNING EVENT_ID INTO RETURNeventid; END IF; INSERT INTO PODS.ILI_INSPECTION_RANGE(EVENT_ID,sequence,ascending,ILI_INSPECTION_ID,DESCRIPTION,COMMENTS,WELD_INCREMENT,SOURCE_GCL,START_ODOMETER, END_ODOMETER, LAUNCHER_EVENT_ID, RECEIVER_EVENT_ID,"weld_start_number")
VALUES(RETURNeventid,1,1,RETURNiliid,v_pipe,'{KM_START}-{KM_END}',null::numeric,'UNKNOWN',null::numeric, null::numeric, (SELECT MIN(lr.event_id) FROM pods.launcher_receiver lr JOIN pods.event_range e ON e.event_id = lr.event_id JOIN pods.station_point sp ON sp.station_id = e.station_id_begin WHERE sp.route_id = {ROUTE_ID} AND lr.type_cl = 'LAUNCH_R_TYPE_06'), (SELECT MAX(lr.event_id) from pods.launcher_receiver lr JOIN pods.event_range e ON e.event_id = lr.event_id JOIN pods.station_point sp ON sp.station_id = e.station_id_begin WHERE sp.route_id = {ROUTE_ID} AND lr.type_cl = 'LAUNCH_R_TYPE_07'), '{call_complex_method.FIRST_WELD_NUMBER}');
END $$;

DO $$

DECLARE
v_coordinate_id NUMERIC(16);
v_report_vendor VARCHAR(254);
v_coordinate_source_operator VARCHAR(16);
v_coordinate_source_id NUMERIC(16);
v_year VARCHAR(4);
v_location_id NUMERIC(16);

BEGIN

IF {X} IS NOT NULL AND {Y} IS NOT NULL
THEN

SELECT vc.description,EXTRACT(YEAR FROM ii.begin_date)::text
INTO v_report_vendor,v_year
FROM pods.ili_inspection ii
JOIN pods.ili_tool_vendor_cl vc
ON ii.tool_vendor_cl = vc.code
WHERE ili_inspection_id = {ILI_INSPECTION_ID};

SELECT MIN(code)
INTO v_coordinate_source_operator
FROM pods.coordinate_source_operator_cl
WHERE description = v_report_vendor;

/* Если не нашелся подходящий, то создам нового*/
IF v_coordinate_source_operator IS NULL
THEN
SELECT 'OPERATOR_'||(MAX((substring(code,'\d.'))::numeric) + 1)::text
INTO v_coordinate_source_operator
FROM pods.coordinate_source_operator_cl
WHERE code like 'OPERATOR%'
AND substring(code,'\d.') IS NOT NULL;

INSERT INTO pods.coordinate_source_operator_cl (code,description,active_indicator_lf,source_gcl)
VALUES (v_coordinate_source_operator,v_report_vendor,'Y','NGG');

END IF;

SELECT MIN(coordinate_source_id)
INTO v_coordinate_source_id
FROM pods.coordinate_source
WHERE operator_cl = v_coordinate_source_operator
AND source_name = v_report_vendor||' ('||v_year||')';

IF v_coordinate_source_id IS NULL
THEN
INSERT INTO pods.coordinate_source (source_name,source_type_cl
,create_date,method_cl,operator_cl,accuracy_cl,coordinate_sys_id)
VALUES (v_report_vendor||' ('||v_year||')','COORD_S_TYPE_06'
,current_timestamp,'INSTRUMENT',v_coordinate_source_operator,'UNKNOWN',4284)
RETURNING coordinate_source_id INTO v_coordinate_source_id;

END IF;

INSERT INTO pods.location (srv_district_gcl)
VALUES ({SRV_DISTRICT_GCL})
RETURNING location_id INTO v_location_id;

INSERT INTO pods.coordinate (coordinate_source_id,type_cl,location_id,x_coord,y_coord,z_coord
,horiz_indicator_lf,vert_indicator_lf,current_indicator_lf,effective_from_date
,accuracy_cl,validity_tolerance,create_date,pods_user)
VALUES (v_coordinate_source_id,'PIG',v_location_id,{X},{Y},{Z}
,'Y',CASE WHEN {Z} IS NOT NULL THEN 'Y' ELSE 'N' END,'Y',current_timestamp
,'UNKNOWN',0,current_timestamp,'ADMIN')
RETURNING coordinate_id INTO v_coordinate_id;

END IF;

INSERT INTO
PODS.ILI_DATA(ILI_INSPECTION_ID,WELD_NUMBER,ABSOLUTE_ODOMETER,AVERAGE_DEPTH,LENGTH,WIDTH,BPR_PIG,INTERNAL_EXTERNAL_CL,SOURCE_GCL,DATE_COLLECTED,ORIENTATION_DEG,ANOMALY_TYPE_CL,ANOMALY_EXTENSION_CL,MEASURED_WALL_THICKNESS,FEATURE_DESCRIPTION,DESCRIPTION,MILEPOST,NOMINAL_WALL_THICKNESS,DS_WELD_DISTANCE,COORDINATE_ID,COMMENTS,US_WELD_ODOMETER,DS_WELD_ODOMETER,US_WELD_NUMBER)
VALUES({ILI_INSPECTION_ID},'{WELD_NUMBER}',{ABSOLUTE_ODOMETER},NULLIF('{AVERAGE_DEPTH}','')::numeric,NULLIF('{LENGTH}','')::numeric,NULLIF('{WIDTH}','')::numeric,NULLIF('{BPR_PIG}','')::numeric,'UNKNOWN','{SOURCE_GCL}',TO_DATE('{DATE}','DD.MM.YYYY'),ROUND(NULLIF('{ORIENTATION_DEG}','')::numeric)
,CASE WHEN '{SOURCE}'='WLD'
THEN 5001
ELSE (SELECT MIN(CODE)
FROM pods.ili_anomaly_type_cl t
WHERE upper(','||coalesce(t.description,'')::text || ',' || coalesce(t.anomaly_references,'')::text) LIKE '%'||REGEXP_REPLACE (UPPER (TRIM ('{ANOMALY_TYPE_CL}')),'\(.*\)|[*.?]|\s+','%')|| '%')
END
,'UNKNOWN',NULL,'{FEATURE_DESCRIPTION}','{DESCRIPTION}','{MILEPOST}',NULLIF('{NOMINAL_WALL_THICKNESS}','')::numeric,{DL_TUBE},v_coordinate_id,'{COMMENTS}',{US_WELD_ODOMETER},{DS_WELD_ODOMETER},'{US_WELD_NUMBER}'); 			
END $$;

